import React from 'react';
import Container from 'react-bootstrap/Container';
import { PageNotFound } from './404_styled';
import { connect } from 'react-redux';
import PageImage from './404_page.png';

const page_404 = props => {
    return (
        <PageNotFound themeStyle={props.themeStyle} theme={props.theme}>
            <Container>
                <div className='img_not_found'>
                    <img className='img-responsive' src={PageImage} />
                </div>
                <div className='text_not_found text-capitalize'>
                    {props.sit_language && props.sit_language.server_connection_error}
                </div>
            </Container>
        </PageNotFound>
    )
}

const mapStateToProps = state => ({
    themeStyle: state.theme.themeStyle,
    theme: state.theme.theme,
    sit_language: state.lang.lang.sit_language
});

export default connect(mapStateToProps)(page_404);
