import React, { useState, useEffect } from 'react'
import { LogInStyled, EmailStyled, PasswordStyled } from './logInStyled';
import { connect } from 'react-redux';
import Form from 'react-bootstrap/Form';
import FormControl from 'react-bootstrap/FormControl';
import FormText from 'react-bootstrap/FormText';
import Button from 'react-bootstrap/Button';
import { Link } from 'react-router-dom';

const Login = props => {

    const [email, setEmail] = useState('');
    const [password, setPassword] = useState('');
    const [emailValid, setEmailValid] = useState(true);
    // const [passwordTouch, setPasswordTouch] = useState(false);

    const submitHandler = (e) => {
        e.preventDefault();
        console.log('Submit');
    }

    const validateEmail = (email) => {
        const re = /^(([^<>()[\]\\.,;:\s@"]+(\.[^<>()[\]\\.,;:\s@"]+)*)|(".+"))@((\[[0-9]{1,3}\.[0-9]{1,3}\.[0-9]{1,3}\.[0-9]{1,3}\])|(([a-zA-Z\-0-9]+\.)+[a-zA-Z]{2,}))$/;
        return re.test(String(email).toLowerCase());
    }

    let x = validateEmail(email);
    console.log('XX => ' + x)

    return (
        <LogInStyled themeStyle={props.themeStyle}>
            <h4 className='title text-capitalize mb-3'>log in</h4>
            <div className='login_social text-center mb-4'>
                <Button className='social_btn face text-capitalize d-block mb-2 w-100 border-0'>Facebook</Button>
                <Button className='social_btn google text-capitalize d-block mb-3 w-100 border-0'>Google</Button>
                <span className='small_message'> or log in with</span>
                {/* <FormText className='error_msg text text-capitalize'>*error on Text</FormText> */}
            </div>
            <div className='my_form'>
                <Form onSubmit={submitHandler}>
                    <EmailStyled emailValid={emailValid} themeStyle={props.themeStyle}>
                        <FormText className='error_msg text text-capitalize'>error on Email</FormText>
                        <FormControl type='email' placeholder='Email' value={email} onChange={e => setEmail(e.target.value)} />
                    </EmailStyled>
                    <PasswordStyled>
                        {/* <FormText className='error_msg pass text-capitalize'>Error on Passowrd</FormText> */}
                        <FormControl type='password' placeholder='Password' value={password} onChange={e => setPassword(e.target.value)} />
                    </PasswordStyled>
                    <Button type='submit' className='submit text-capitalize d-block border-0 mt-4 w-100'>login</Button>
                </Form>
            </div>
            <div className='dont_have_account mt-5 text-center'>
                <p>Don't have account <Link to=''>Sign up</Link></p>
            </div>
        </LogInStyled>
    )
}

const mapStateToProps = state => ({
    themeStyle: state.theme.themeStyle
})

export default connect(mapStateToProps)(Login);
