import styled from 'styled-components';

const HomeStyled = styled.div`
    & .main-content-show {
        color: #fff;
        display: flex;
        flex-wrap: wrap;
    }
    & .search_select {
        .form-select .form-group {
            .select {
                border-radius: 800px;
                background-color: #f5b44b;
                border: 1px solid #f5b44b;
                color: #fff;
                box-shadow: 0 2px 8px 0 rgb(247 185 82 / 41%);
                option {
                    background-color: #fff;
                    color: #333;
                }
            }
        }
    }
    & .show-all-names {
        text-align: start;
    }
`;
