import React from 'react'
import { LogInStyled } from './logInStyled';
import { connect } from 'react-redux';
import Form from 'react-bootstrap/Form';
import FormControl from 'react-bootstrap/FormControl';
import FormText from 'react-bootstrap/FormText';
import { Link } from 'react-router-dom';

const Login = props => {
    return (
        <LogInStyled themeStyle={props.themeStyle}>
            <h3 className='title text-capitalize mb-4'>log in</h3>
            <div className='login_social text-center'>
                <button className='btn social_btn text-capitalize face'>Facebook</button>
                <button className='btn social_btn text-capitalize google'>Google</button>
                <span className='small_message'> or log in with</span>
            </div>
            <div className='my_form'>
                <Form>
                    <FormControl type='text' placeholder='Username' />
                    <FormText className='error_msg_text'>error on Text</FormText>
                    <FormControl type='password' placeholder='Password' />
                    <FormText className='error_msg_pass'>Error on Passowrd</FormText>
                </Form>
            </div>
            <div className='dont_have_account mt-5 text-center'>
                {/* <p>Don't have account <Link to=''>Sign up</Link></p> */}
            </div>
        </LogInStyled>
    )
}

const mapStateToProps = state => ({
    themeStyle: state.theme.themeStyle
})

export default connect(mapStateToProps)(Login);
