import React, { useEffect } from 'react';
import { Redirect } from 'react-router-dom';
import { connect } from 'react-redux';

const RedirectMain = props => {
    useEffect(() => {
        console.log(props);
    }, [props.language.lang]);
    // console.log(props);
    
    return props.language.lang && <Redirect to={`/${props.language.lang}`}/>
}

const mapStateToProps = state => ({
    language: state.language
});

export default connect(mapStateToProps)(RedirectMain);
