import React, { useState, useRef, useCallback } from 'react';
import { PlayerContent } from './audioPlayerStyled';
import { AiOutlineClose } from 'react-icons/ai';
import { FaRandom } from 'react-icons/fa';
import { CgTrashEmpty } from 'react-icons/cg';
import { AiFillHeart } from 'react-icons/ai';
import { BsArrowRight } from 'react-icons/bs';
import Container from 'react-bootstrap/Container';
import { connect } from 'react-redux';
import * as actions from '../../store/action/getData';
import Playlist from '../playlist/playlist';
import AudioPlayer from 'react-h5-audio-player';
import 'react-h5-audio-player/lib/styles.css';


const SongPlayer = props => {
    const [showfavPlaylist, setShowfavPlaylist] = useState(false);
    const favPlaylistclasses = showfavPlaylist ? ['playlist_list', 'show'] : ['playlist_list'];
    const [autoPlayAudio, setAutoPlayAudio] = useState(false);

    const randomChoice = () => {
        console.log('Random');
        if(props.isRandom) {
            const getRandom = Math.floor(Math.random() * props.suras.length).toString()
            const num = props.suras[getRandom].length === 1 ? `00${props.suras[getRandom]}` : props.suras[getRandom].length === 2 ? `0${props.suras[getRandom]}` : props.suras[getRandom];
            props.suraLinkRandom(num);
            let getName = props.allSurasNames[getRandom];
            props.changeName(getName);
            props.isFromFavouritePlaylist(false);
            props.imamNameFunc(props.imamName);
            console.log('{{{{{{{{{{{{{{{{{{{{{{{}}}}}}}}}}}}}}}}}}}}}}}}}}}')
        }
    }

    const deleteItems = (item) => {
        console.log(item.server);
        props.deleteFavFromSurabox(item.server);
        props.deleteItemFromPlaylist(item);
    }

    const clickedPlaylist = (link, number, suraName, imamName) => {
        props.playingFalse();
        props.suraLinkFunc(link);
        props.getSurah_Number_Name(number, suraName);
        props.showPlayer();
        props.imamNameFunc(imamName);
        props.isFromFavouritePlaylist(true);
        props.favouritePlaylistImmamName(imamName);
        props.emptySuras();
    }

    console.log('SONG Player');

    return (
        <PlayerContent suras={props.suras} theme={props.theme} themeStyle={props.themeStyle} isFavPlaylist={props.isFavPlaylist} showfavPlaylist={showfavPlaylist} autoPlayAudio={autoPlayAudio} >
            {!props.isFavPlaylist && (
                <div className='random d-none d-sm-block' onClick={randomChoice} title={props.language.lang === 'ar' ? 'عشوائي' : 'Random'}>
                    <FaRandom size={22} />
                </div>
            )}
            <div className='favorite_box d-none d-sm-block' title={props.site_language.favorite}>
                <AiFillHeart size={24}  onClick={() => setShowfavPlaylist(!showfavPlaylist)} />
            </div>
            <div className='autoPlay_box d-none d-sm-block' title={props.site_language.autoplay_next_surahs}>
                <BsArrowRight size={30}  onClick={() => setAutoPlayAudio(autoPlayAudio => !autoPlayAudio)}/>
            </div>
            <AudioPlayer 
                autoPlay
                src={props.suraLink}
                onEnded={e => {
                    let regex = /\d+/g;
                    let string = props.suraLink;
                    let matches = +string. match(regex)[2];
                    let number = null;
                
                    props.suras.map((element, index) => {
                        if(element == matches) {
                            number = index + 1;
                        }
                    })

                    if(autoPlayAudio && props.suras[number] !== undefined) {
                        let numberString = props.suras[number].length === 1 ? `00${props.suras[number]}` : props.suras[number].length === 2 ? `0${props.suras[number]}` : props.suras[number];
                        props.suraLinkFunc(`${props.server}/${numberString}.mp3`);
                    }


                    console.log('Ended')
                }}
            />
            <Container>
                <div className='playlist_items d-none d-sm-block'>
                    <div className={favPlaylistclasses.join(' ')}>
                        <div className='header d-flex'>
                            <AiOutlineClose size={20} onClick={() => setShowfavPlaylist(!showfavPlaylist)} />
                            <h5 className='m-auto'>{props.site_language.favorite}</h5>
                        </div>
                        <ul className='list-unstyled sura_list'>
                            {
                                props.playlist && props.playlist.length > 0 
                                ?  props.playlist.map((e, i) => (
                                    <Playlist
                                        key={e.server}
                                        server={e.server}
                                        number={e.number}
                                        name={e.name}
                                        rewaya={e.rewaya}
                                        index={i}
                                        suraName={e.suraName}
                                        deleteItem={(item) => deleteItems(item)}
                                        clicked={(link, number, suraName, name) => clickedPlaylist(link, number, suraName, name)}
                                    />
                                ))
                                : <div className='no_list text-center text-capitalize'>{props.site_language.no_playlist_added} <CgTrashEmpty size={26} /></div>
                            }
                        </ul>
                    </div>
                </div>
            </Container>
        </PlayerContent>
    );
};

const mapStateToProps = state => {
    return {
        imamName: state.getData === null ? '' : state.getData.name,
        suraName: state.suraName,
        suraLink: state.suraLink,
        isPlaying: state.isPlaying,
        suras: state.suras,
        server: state.server,
        allSurasNames: state.allSurasNames,
        isImamPage: state.isImamPage,
        imamNameBeforeUpdate: state.imamNameBeforeUpdate,
        isRandom: state.isRandom,
        playlist: state.playlist,
        isFavPlaylist: state.isFavPlaylist,
        playlistImamName: state.playlistImamName,
        theme: state.theme,
        themeStyle: state.themeStyle,
        language: state.language,
        site_language: state.site_language
    }
}

const mapDispatchToProps = dispatch => ({
    togglePlay: () => dispatch(actions.playingToggle()),
    playingFalse: () => dispatch(actions.playingFalse()),
    playingTrue: () => dispatch(actions.playingTrue()),
    suraLinkRandom: (data) => dispatch(actions.suraLinkRandom(data)),
    changeName: (name) => dispatch(actions.changeName(name)),
    imamNameFunc: (name) => dispatch(actions.getImamNameBeforeUpdate(name)),
    deleteItemFromPlaylist: (item) => dispatch(actions.removeItemFromPlayList(item)),
    deleteFavFromSurabox: (item) => dispatch(actions.deleteFavFromSurabox(item)),
    showPlayer: () => dispatch(actions.showPlayer()),
    suraLinkFunc: (link) => dispatch(actions.suraLink(link)),
    getSurah_Number_Name: (number, suraName) => dispatch(actions.getSurah_Number_Name(number, suraName)),
    isFromFavouritePlaylist: (bool) => dispatch(actions.isFromFavouritePlaylist(bool)),
    favouritePlaylistImmamName: (name) => dispatch(actions.favouritePlaylistImmamName(name)),
    emptySuras: () => dispatch(actions.emptySuras()),
});

export default connect(mapStateToProps, mapDispatchToProps)(SongPlayer);
