import React, { lazy, Suspense, useEffect, useState } from 'react';
import './App.css';
import 'bootstrap/dist/css/bootstrap.min.css';
import { Route, Switch } from 'react-router-dom';
import Layout from '../components/layout/layout';
import Loading from '../components/UI/Loader/loader';
import {connect} from 'react-redux';

// import Main from '../components/MainContent/main';
// import Search from './containers/Search/search';
// import Library from './containers/Library/library';
// import Favorite from './containers/Favorite/favorite';

// const AsyncSearch = lazy(() => import('../containers/Search/search'));
// const AsyncLibrary = lazy(() => import('../containers/Library/library'));
// const AsyncFavorite = lazy(() => import('../containers/Favorite/favorite'));

const AsyncMain = lazy(() => import('../components/MainContent/main'));
const AsyncImamPage = lazy(() => import('../components/imamPage/ImamPage'));

const App = React.memo(props => {

  const [playlistStorage, setPlaylistStorage] = useState(null);

  useEffect(() => {
    const getPlaylistFromLocalStorage = localStorage.getItem('playlist');
    if(getPlaylistFromLocalStorage) {
      setPlaylistStorage(JSON.parse(getPlaylistFromLocalStorage));
    }
  }, []);

  useEffect(() => {
    localStorage.setItem('playlist', JSON.stringify(props.playlist));
  });


  let routes = (
    <Switch>
      <Route path='/'         render={props => <AsyncMain {...props} />} exact />
      <Route path='/imamPage' render={props => <AsyncImamPage {...props} />} />
      {/* <Route to='/search'   render={props => <AsyncSearch {...props} /> } />
      <Route to='library'   render={props => <AsyncLibrary {...props} />} />
      <Route to='favorite'  render={props => <AsyncFavorite {...props} />} /> */}
    </Switch>
  )
  return (
    <div className="App">
      <Layout>
        <Suspense fallback={<Loading />}>{routes}</Suspense>
      </Layout>
    </div>
  );
});

const mapStateToProps = state => ({
  playlist: state.playlist
});

export default connect(mapStateToProps)(App);
