import React, { useEffect } from 'react';
import Letter from '../letters/letters';
import withErrorHandler from '../../hoc/withErrorHandler/withErrorHandler';
import axios from 'axios';
import ImamBoxs from '../imamBoxs/imamBoxs';
import './main.css';
import * as actions from '../../store/action/getData';
import { connect } from 'react-redux';
import Loader from '../UI/Loader/loader';

const Main = (props) => {
    let url = 'http://mp3quran.net/api/_english.json';
    useEffect(() => {
        let source = axios.CancelToken.source();
        props.startMain();
        axios.get(url, {
            cancelToken: source.token
        })
        .then(res => {
            
            const arrayOfLetters = [];
            const allElementsArrays = [];
            const arrayOfLettersIndex = [];
            const allArraysElements = res.data.reciters;
            const myData = allArraysElements.map(letter => letter.letter);
            myData.filter((element, index) => element !== myData[index + 1] && arrayOfLetters.push(element));
            myData.filter((element, index) => element !== myData[index + 1] && arrayOfLettersIndex.push(index));
            allArraysElements.map(element => allElementsArrays.push(element));
            
            props.successMain(arrayOfLetters, arrayOfLettersIndex, allElementsArrays);

        })
        .catch(error => props.failRequest(error));
        return () => {
            source.cancel();
        }
    }, [url]);
    
    let fullElementSections = [];
    let curentIndex = 0;

    const get = React.useMemo(() => {
        if(props.letters && props.allElementsImams && props.lettersIndex) {
            for(let i = 0; i < props.letters.length; i++) {
                let speElement = props.allElementsImams.slice(curentIndex , props.lettersIndex[i] + 1);
                curentIndex= props.lettersIndex[i] + 1;
                fullElementSections.push({
                    letterSection: props.letters[i],
                    sectionElements: speElement
                });
            }
        }
    }, [fullElementSections]);

    let show = <Loader />;

    if(!props.loading) {
        show = (
            <div className='show-names-letter'>
                <div className='main-content-show'>
                    {props.letters && props.letters.map(letter => <Letter text={letter} id={`#letter_${letter}`} key={letter} />) }
                </div>
                <div className='show-all-names'>
                    {
                        props.allElementsImams && props.allElementsImams.length > 0 && fullElementSections.map((element, index) => {
                            // console.log(element)
                            return (   
                                <ImamBoxs
                                    key={element.letterSection + element.letterSection}
                                    letter={element.letterSection}
                                    boxes={element.sectionElements}
                                    myIndex={index}
                                />
                            )
                        })
                    }
                </div>
            </div>
        )
    }
    return show;
};

const mapStateToProps = state => ({
    loading: state.loading,
    letters: state.letters,
    lettersIndex: state.lettersIndex,
    allElementsImams: state.allElementsImams
});

const mapDispatchToProps = dispatch => ({
    startMain: () => dispatch(actions.startMain()),
    successMain: (letters, lettersIndex, allEllemnt) => dispatch(actions.successMain(letters, lettersIndex, allEllemnt)),
    failRequest: () => dispatch(actions.failRequest()),
})

export default connect(mapStateToProps, mapDispatchToProps)(withErrorHandler(Main, axios));
