import React from 'react';
import { BiDownload } from 'react-icons/bi';
import axios from 'axios';
import { connect } from 'react-redux';

const downloadBtn = props => {
    
    const downloadSura = () => {
        axios.get(props.url, {
            responseType: 'arraybuffer',
            headers: {
                'Content-Type': 'audio/mpeg'
            }
        }).then(response => {
            const url = window.URL.createObjectURL(new Blob([response.data]));
            const link = document.createElement('a');
            link.href = url;
            link.setAttribute('download', `${props.suraName}.mp3`);
            document.body.appendChild(link);
            link.click();

        }).catch(error => error);
    }

    return (
        <button className='btn' onClick={downloadSura} title={props.site_language && props.site_language.download}>
            <BiDownload size={props.size} />
        </button>
    );
}

const mapStateToProps = state => ({
    site_language: state.lang.site_language
});

export default connect(mapStateToProps)(downloadBtn);
