import React from 'react';
import './user.css';
import { FaUserAlt } from 'react-icons/fa';
// import { UserStyled } from './userStyled';
import { connect } from 'react-redux';

const user = props => {
    const cutSentance = name => name.length > 8 ? `${name.slice(0, 8)}...` : name; 
    return (
        // <UserStyled theme={props.theme} themeStyle={props.themeStyle} className='d-flex align-items-center justify-content-between'>
        <div className='d-flex align-items-center justify-content-between'>
            <div className='img_logo rounded-circle d-flex align-items-center justify-content-center'>
                <FaUserAlt size={props.imgSize}/>
                {/* <img className='img-fluid rounded' src='' /> */}
            </div>
            <h6 className='useName m-0 text-capitalize'>{cutSentance('Mohammed')}</h6>
        </div>
    )
};

const mapStateToProps = state => ({
    theme: state.theme,
    themeStyle: state.themeStyle
});

export default connect(mapStateToProps)(user);
