import styled from 'styled-components';

export const AsideLink = styled.div`
    & > a {
        color: ${props => props.theme === 'light' ?  '#636a6f' : props.themeStyle.all.whiteColor};
    }
    & > a:before {
        content: '';
        position: absolute;
        top: 0;
        width: 3px;
        height: 100%;
        z-index: -1;
    }
    & > a.active:before,
    & > a:hover:before
    {
        content: '';
        ${props => props.site_lang === 'eng' ? 'left' : 'right'}: ${props => props.theme === 'light' ? '2px' : '3px'};
        background-color: ${props => props.themeStyle.all.mainColor};
    }
    & > a.active {
        color: ${props => props.themeStyle.all.mainColor};
    }
    & > a .aside-list-logo {
        color: ${props => props.theme === 'light' ? '#636a6f' : props.themeStyle.all.whiteColor};
    }
    & > a.active .aside-list-logo {
        color: ${props => props.themeStyle.all.mainColor};
    }
`;
