import React, { useState, useEffect, useRef } from 'react';
import { RadioStyled } from './RadiosStyle';
import { connect } from 'react-redux';
import axios from 'axios';
import Form from 'react-bootstrap/Form';
import withErrorHandler from '../../hoc/withErrorHandler/withErrorHandler';
import RadioList from './radioList';
import * as actions from '../../store/action/getData';

const Radios = props => {

    const [radioLists, setRadioLists] = useState([]);
    const [copyRadioLists, setCopyRadioLists] = useState();
    const [search, setSearch] = useState('');
    const isMounted = useRef(false);

    useEffect(() => {
        props.isFavoritePage(true);
        props.isRadioPage(true);
    }, []);

    useEffect(() => {
        isMounted.current = true;
        axios.get(`http://mp3quran.net/api/2/radio_${props.language.lang}.json`)
        .then(res => {
            if(isMounted.current) {
                setTimeout(() => {
                    setRadioLists(res.data.Radios);
                    setCopyRadioLists(res.data.Radios);
                }, 2000);
                // setRadioLists(res.data.Radios);
                // setCopyRadioLists(res.data.Radios);
            }
        })
        .catch(error => {
            if(isMounted.current) {
                return error
            }
        });

        return () => {isMounted.current = false};
    }, [props.language]);

    useEffect(() => {
        isMounted.current = true;
        if(isMounted.current) {
            let favArray = [];
            radioLists.map(radiosList => {
                if (search === '') {
                    console.log('QQQ');
                    favArray.push(radiosList);

                } else if (radiosList.Name.toLocaleLowerCase().includes(search.toLocaleLowerCase())) {
                    favArray.push(radiosList)
                }
            });
            setCopyRadioLists(favArray);
        }
        
        return () => { isMounted.current = false };
    }, [search]);

    const clickedHandler = (link) => {
        props.suraLinkFunc(link.url);
        props.showPlayer();
    }

    console.log('Radios');

    return (
        <RadioStyled language={props.language.lang} themeStyle={props.themeStyle} theme={props.theme}>
            <h2 className='title_name mb-4'>{props.site_language && props.site_language.quran_radio}</h2>
            <div className='form-search mb-5 mb-lg-0'>
                <Form>
                    <Form.Control type='text' placeholder={`${props.site_language && props.site_language.search} ${props.site_language && props.site_language.reciter}`} className='rounded-pill search-filed' onChange={e => setSearch(e.target.value)} dir='auto' />
                </Form>
            </div>
            <ul className='radios_lists'>
                {
                    copyRadioLists && copyRadioLists.length > 0 && copyRadioLists.map(radio => {
                        return (
                            <RadioList
                                key={radio.URL}
                                url={radio.URL}
                                name={radio.Name}
                                themeStyle={props.themeStyle.all.yellowColor}
                                clicked={(link) => clickedHandler(link)}
                                sameLink={radio.URL === props.suraLink}
                            />
                        )
                    })
                }
            </ul>
        </RadioStyled>
    )
}

const mapStateToProps = state => ({
    site_language: state.site_language,
    language: state.language,
    theme: state.theme,
    themeStyle: state.themeStyle,
    suraLink: state.suraLink
});

const mapDispatchToProps = dispatch => ({
    isFavoritePage: (value) => dispatch(actions.isFavoritePage(value)),
    showPlayer: () => dispatch(actions.showPlayer()),
    isRadioPage: (value) => dispatch(actions.isRadioPage(value)),
    suraLinkFunc: (link) => dispatch(actions.suraLink(link))
});

// export default connect(mapStateToProps, mapDispatchToProps)(Radios);

export default connect(mapStateToProps, mapDispatchToProps)(withErrorHandler(Radios, axios));
