import React, { useEffect, useState } from 'react';
import User from '../../user/user';
import { AiFillHome, AiFillHeart } from 'react-icons/ai';
import { NavLink, Link } from 'react-router-dom';
import { FaBroadcastTower } from 'react-icons/fa';
import Form from 'react-bootstrap/Form';
import { IoLogOutOutline } from 'react-icons/io5';
import { IoMdCloseCircle } from 'react-icons/io';
import { connect } from 'react-redux';
import * as actions from '../../../store/action/getData';
import Modal from 'react-bootstrap/Modal';
import { SidebarStyled } from './sidebarStyled';

const Sidebar = props => {

    const [chooseThemeValue, setChooseThemeValue] = useState(false);

    useEffect(() => {
        setChooseThemeValue(props.isThemeDark);
    }, [props.isThemeDark]);

    const chooseTheme = event => {
        setChooseThemeValue(event);
        props.toggleTheme(event);
        localStorage.setItem('isThemeIsDark', JSON.stringify(event));
    };

    const chooseLanguage = (event) => {
        console.log(event)
    }

    const classes = props.isAsideShow ? ['sidebar_menu', 'show'] : ['sidebar_menu'];
    const classesBtn = props.isAsideShow ? ['closeBtn', 'btn', 'show'] : ['closeBtn', 'btn'];

    const closeAsideByClass = (e) => {
        // if(e.target.className === 'sc-eCssSg' || e.target.className === 'bhNnkz' || e.target.className === 'gafibl') {
        // if(e.target.className === 'sc-iBPRYJ gafibl' || e.target.className === 'sc-iBPRYJ bhNnkz') {
        // if(e.target.className.contains('sc-iBPRYJ')) {
        if(e.target.classlist.contains('sc-iBPRYJ')) {
        // if(e.target.className.includes(sc-iBPRYJ 'bhNnkz')) {
        // if(['sc-iBPRYJ', 'bhNnkz', 'gafibl'].includes(e.target.className)) {
            console.log('Close Up');
            // props.closeAside();
        }
    };

    return (
        <div className='side_nave'>
            <Modal show={props.isAsideShow} animation={false} onClick={(e) => closeAsideByClass(e)}>
                <SidebarStyled theme={props.theme} themeStyle={props.themeStyle}>
                    <div className={classes.join(' ')}>
                        <button className={classesBtn.join(' ')} onClick={props.closeAside}>
                            <IoMdCloseCircle size={35} />
                        </button>
                        <div className='user_name_logo'>
                            <User imgSize={16} />
                        </div>
                        <hr className='divider'/>
                        <ul className='nav_side list-unstyled'>
                            <li className='list' onClick={props.closeAside}>
                                <NavLink to='/' className='d-flex align-items-center' exact>
                                    <span className='nav_side_icon'>
                                        <AiFillHome size={20} />
                                    </span>
                                    <span className='nav_side_text text-capitalize'>Home</span>
                                </NavLink>
                            </li>
                            <li className='list' onClick={props.closeAside}>
                                <NavLink to='/favorite' className='d-flex align-items-center'>
                                    <span className='nav_side_icon'>
                                        <AiFillHeart size={20} />
                                    </span>
                                    <span className='nav_side_text text-capitalize'>favorite</span>
                                </NavLink>
                            </li>
                            <li className='list' onClick={props.closeAside}>
                                <NavLink to='/live' className='d-flex align-items-center'>
                                    <span className='nav_side_icon'>
                                        <FaBroadcastTower size={20} />
                                    </span>
                                    <span className='nav_side_text text-capitalize'>live radio</span>
                                </NavLink>
                            </li>
                        </ul>
                        <hr className='divider'/>
                        <div className='text-section choose_mode'>
                            <h6 className='title-section text-capitalize mb-3'>choose mode:</h6>
                            <Form className='d-flex align-items-center myForm'>
                                <Form.Label>Light</Form.Label>
                                <Form.Check 
                                    type="switch"
                                    id="custom-switch"
                                    label="Dark"
                                    defaultChecked={chooseThemeValue}
                                    onChange={(e) => chooseTheme(e.target.checked)}
                                />
                            </Form>
                        </div>
                        <hr className='divider'/>
                        <div className='text-section choose_language'>
                            <h6 className='title-section text-capitalize mb-3'>choose Language:</h6>
                            <Form>
                                <Form.Group controlId="exampleForm.SelectCustom" onChange={(e) => chooseLanguage(e.target.value)}>
                                    <Form.Control as="select" custom>
                                        <option className='text-capitalize' value='ar'>العربية</option>
                                        <option className='text-capitalize' value='eng'>English</option>
                                        <option className='text-capitalize' value='fr'>Français</option>
                                        <option className='text-capitalize' value='ru'>Русский</option>
                                        <option className='text-capitalize' value='de'>Deutsch</option>
                                        <option className='text-capitalize' value='es'>Español</option>
                                        <option className='text-capitalize' value='tr'>Türkçe</option>
