const mapStateToProps = state => ({
    isAsideShow: state.isAsideShow,
    theme: state.theme,
    themeStyle: state.themeStyle,
    isThemeDark: state.isThemeDark
});

const mapDispatchToProps = dispatch => ({
    closeAside: () => dispatch(actions.closeAside()),
    openAside: () => dispatch(actions.openAside()),
    // changeTheme: () => dispatch(actions.changeTheme()),
    toggleTheme: (value) => dispatch(actions.toggleTheme(value))
});

export default connect(mapStateToProps, mapDispatchToProps)(Sidebar);
