import React, { useReducer, useCallback, useState, useEffect } from 'react';
import { SurahBoxList } from './surahBoxStyled';
import PlayBtn from '../playBtn/playBtn';
import HeartBtn from '../heartBox/heartBox';
import LinkBtn from '../linkBtn/linkBtn';
import DownloadBtn from '../downloadBtn/downloadBtn';
import DotBtn from '../DotBtn/DotBtn';
import ToastComp from '../toastCompo/toastCompo';
import { connect } from 'react-redux';
import * as actions from '../../store/action/getData';


const initialState = (state, action) => {
    switch(action.type) {
        case 'isToastOpen':
            return {...state, isToastOpen: true, msg: action.msg, isFavorite: action.isFavorite}
        case 'isToastClose':
            return {...state, isToastOpen: false, msg: {}, isFavorite: state.isFavorite}
        case 'isFavorite':
            return {...state, isFavorite: action.isFavorite}
        case 'Msg':
            return {...state, msg: action.msg}
        default: 
        return state;
    }
}

const SurahBox = props => {
    const [surahState, dispatch] = useReducer(initialState, {
        isToastOpen: false,
        isFavorite: props.isFavor,
        msg: {}
    });

    const [isFav, setIsFav] = useState(props.isFavor);
    const [linkState, setLinkState] = useState(false);

    const toggleFav = () => {
        if(isFav) {
            props.playlist.map(list => {
                if(list.server === `${props.allData.Server}/${props.number}.mp3` && list.number === props.number && list.suraName === props.name) {
                    props.deleteItemFromPlaylist({server: list.server});
                }
            });
        }
        setIsFav(!isFav);
    };

    useEffect(() => {
        if(`${props.server}/${props.number}.mp3` === props.deleteFav) {
            setIsFav(false);
        }
    }, [props.deleteFav]);

    useEffect(() => {
        if(props.suraLink === props.copyText) {
            setLinkState(true);
            console.log(props.suraLink + ' =>>> ' + props.copyText);
        } else {
            setLinkState(false);
        }
    }, [props.suraLink]);

    const setTime = () => setTimeout(() => {closeToast()}, 5000);

    const openToast = useCallback((msg) => {
        dispatch({type: 'isToastOpen', msg: {...msg}, isFavorite: msg.isFavorite });
    }, []);

    const closeToast = useCallback(() => {
        clearTimeout(setTime);
        dispatch({type: 'isToastClose', msg: {}, isFavorite: surahState.isFavorite});
    }, []);

    const cpiedFunc = useCallback(() => {
        clearTimeout(setTime);
        openToast({title: props.language.lang === 'ar' ? 'تم النسخ' : 'Copied', myMsg: props.language.lang === 'ar' ? 'تم نسخ الرابط' : 'The Link is copied', myClass: 'added_item', isFavorite: surahState.isFavorite});
        navigator.clipboard.writeText(props.copyText);
    }, []);

    const favFunc = () => {
        clearTimeout(setTime);
        if(surahState.isFavorite) {
            openToast({title: props.site_language.delete, myMsg: props.site_language.delete + ' ' + props.site_language.favorite, myClass:'remove_item', isFavorite: surahState.isFavorite});
            dispatch({type: 'isFavorite', msg: 'the item was removed from favorites', isFavorite: !surahState.isFavorite });
        } else {
            openToast({title: props.site_language.add, myMsg: props.site_language.adding_done_susseccfully, myClass: 'added_item', isFavorite: surahState.isFavorite});
            dispatch({type: 'isFavorite', msg: 'the item was removed from favorites', isFavorite: !surahState.isFavorite });
        }
    }

    surahState.isToastOpen ? setTime() : clearTimeout(setTime);
    
    const addToFavList = () => {
        favFunc();
        props.addFav({isFavorite: !surahState.isFavorite, allData: props.allData });
    }
    
    const downloadSura = () => {
        console.log(props.copyText)
    }

    return props.site_language && (
        <>
            {surahState.isToastOpen && <ToastComp msg={surahState.msg} />}
            <SurahBoxList className='d-flex justify-content-between flex-wrap pr-1 pl-1 pr-sm-3 pl-sm-3' title={props.name} theme={props.theme} themeStyle={props.themeStyle} linkState={linkState}>
                <div className='icon_name_number d-flex align-items-center' onClick={props.clicked}>
                    <PlayBtn size={30} clicked={props.isPlayToggle} linkState={linkState} />
                    <div className='surah-box-number' onClick={props.isPlayToggle}>{props.number}</div>
                    <div className='surah-box-name text-capitalize' onClick={props.isPlayToggle}>{props.name}</div>
                </div>
                <div className='surah-box-imam-page-info d-flex align-items-center'>
                    <div className='list hide_on_hover m-0' title='copy link' onClick={() => cpiedFunc()}>
                        <LinkBtn />
                    </div>
                    <div className='list hide_on_hover heart m-0' title={props.isFavor ? props.site_language.delete + ' ' + props.site_language.favorite : props.site_language.add_files_playlist} onClick={addToFavList} >
                        <HeartBtn size={22} server={props.server} number={props.number} isFav={isFav} toggleFav={toggleFav} />
                    </div>
                    <div className='list hide_on_hover heart m-0' title='download' >
                        <DownloadBtn size={22} clicked={downloadSura} />
                    </div>
                    <div className='list show_on_hover m-0'>
                        <DotBtn />
                    </div>
                </div>
            </SurahBoxList>
        </>
    );
};

const mapStateToProps = state => ({
    suraLink: state.suraLink,
    playlist: state.playlist,
    deleteFav: state.deleteFav,
    theme: state.theme,
    themeStyle: state.themeStyle,
    language: state.language,
    site_language: state.site_language
});

const mapDispatchToProps = dispatch => ({
    deleteItemFromPlaylist: (item) => dispatch(actions.removeItemFromPlayList(item))
});

export default connect(mapStateToProps, mapDispatchToProps)(SurahBox);
