import styled from 'styled-components';

export const HeaderTitleStyled = styled.div`
    background-color: ${props => props.theme === 'light' ? '#f5f5f5' : '#525960'};
    // background-color: #f5f5f5;
    text-align: center;
    border-bottom: 1px solid #d9dde2;
    padding: 20px;
    height: 145px;
    text-align: start;
    transition: all 0.3s ease-in-out;
    .headerTitle_img {
        width: 175px;
        margin: auto;
    } 
    .headerTitle_text h4 {
        color: #39c2c9;
        @media (max-width: 576px) {
            font-size: 24px;
        }
    }
`;
