import styled from 'styled-components';

export const FormSelected = styled.div`
& > select {
    border-radius: 800px;
    background-color: ${props => props.themeStyle.all.secondaryColor};
    border: 1px solid ${props => props.themeStyle.all.secondaryColor};
    color: ${props => props.themeStyle.all.whiteColor};
    box-shadow: 0 2px 8px 0 rgb(247 185 82 / 41%);
}
`;
