import React, { useState, useRef, useEffect, useCallback } from 'react';
import { PlayerContent } from './songPlayerStyled';
import './songPlayerStyle.css';
import { AiOutlinePlus, AiOutlineMinus, AiOutlineClose } from 'react-icons/ai';
import { FaRandom } from 'react-icons/fa';
import { BiPlay, BiPause } from 'react-icons/bi';
import { BsVolumeDown, BsVolumeMute, BsVolumeUp } from 'react-icons/bs';
import { CgTrashEmpty } from 'react-icons/cg';
import Container from 'react-bootstrap/Container';
import { connect } from 'react-redux';
import * as actions from '../../store/action/getData';
import Playlist from '../playlist/playlist';
import { AiFillHeart } from 'react-icons/ai';


const SongPlayer = React.memo(props => {
    const [value, setValue] = useState('1');
    const [getDuration, setGetDuration] = useState('');
    const [getCurrentTime, setGetCurrentTime] = useState('');
    const myAudioRef = useRef();

    const playFun = useCallback(() => {
        if (myAudioRef.current.paused) {
            myAudioRef.current.play().catch(error => console.log('myError'));
            props.playingFalse();
        }
        console.log('PLay');
    }, []);

    const pauseFun = useCallback(() => {
        if (!myAudioRef.current.paused) {
            myAudioRef.current.pause();
            props.playingTrue();
        }

        console.log('Pause');
    }, []);

    const toggleAudio = useCallback(() => {
        console.log('*************************************************************************')
        props.togglePlay();
        myAudioRef.current.paused ? playFun() : pauseFun();
    }, [])

    const valueHanler = useCallback((event) => {
        console.log(myAudioRef.current.duration)
        setValue(+event.target.value);
        myAudioRef.current.volume = event.target.value;
    }, []);

    useEffect(() => {
        let isMount = false;
        if(!isMount) {
            var au = document.getElementById("my-audio");
            console.log('MNMNMN');
            au.onloadedmetadata = function () {
                let calcDuration = Math.floor(au.duration / 60) + ':' + ('0' + Math.floor(au.duration % 60)).slice(-2)
                setGetDuration(calcDuration);
                let calcCurreentTime = Math.floor(au.currentTime / 60) + ':' + ('0' + Math.floor(au.currentTime % 60)).slice(-2)
                setGetCurrentTime(calcCurreentTime);
            };

            myAudioRef.current.addEventListener('timeupdate', () => {
                let curTime = myAudioRef.current.currentTime;

                let calCurTime = Math.floor(curTime / 60) + ':' + ('0' + Math.floor(curTime % 60)).slice(-2)
                setGetCurrentTime(calCurTime);

                let progress = document.getElementById('short-progress');
                let percent = (curTime / myAudioRef.current.duration) * 100;
                progress.style.flexBasis = `${percent}%`;

                if(myAudioRef.current.currentTime === myAudioRef.current.duration) {
                    console.log('Is Completed')
                    props.playingTrue();
                }
            });

            playFun();
        }
        
        return () => {
            isMount = true;
            myAudioRef.current.removeEventListener('ended', () => props.playingFalse());
        };

    }, [setGetCurrentTime, props.suraLink]);

    const skippedTime = useCallback((e) => {
        myAudioRef.current.currentTime += +e
    }, [])

    const scrub = useCallback((e) => {
        let fullProgress = document.getElementById('full-progress');
        const scrubTime = (e.nativeEvent.offsetX / fullProgress.offsetWidth) * myAudioRef.current.duration;
        myAudioRef.current.currentTime = scrubTime;
    }, []);

    const randomChoice = () => {
        if(props.isRandom) {
            const getRandom = Math.floor(Math.random() * props.suras.length).toString()
            const num = props.suras[getRandom].length === 1 ? `00${props.suras[getRandom]}` : props.suras[getRandom].length === 2 ? `0${props.suras[getRandom]}` : props.suras[getRandom];
            props.suraLinkRandom(num);
            let getName = props.allSurasNames[getRandom];
            props.changeName(getName);
            props.imamNameFunc(props.imamName)
            console.log(num);
            console.log('{{{{{{{{{{{{{{{{{{{{{{{}}}}}}}}}}}}}}}}}}}}}}}}}}}')
        }
    }

    const calcTimes = useCallback(time => {
        const timeInArr = time.split(':');
        const firstElement = +timeInArr[0]
        let myTime = '';
        if(firstElement > 59) {
            let hour = firstElement/60;
            let diviedMin = hour.toString().split('.');
            let getMun = '0.' + diviedMin[1];
            let calcMin = Math.round(getMun * 60);
            myTime = `${diviedMin[0]}:${calcMin < 9 ? `0${calcMin}`:calcMin}:${timeInArr[1]}`;
        } else {
            let minutesInArr = time.split(':');
            let firstElementInMin = +minutesInArr[0];
            let caclfirstElementInMin = firstElementInMin < 9 ? `0${firstElementInMin}` : firstElementInMin;
            myTime = `00:${caclfirstElementInMin}:${minutesInArr[1]}`;
        }
        return myTime;
    }, []);

    const fullTime = calcTimes(getDuration);
    const currentTimeAudio = calcTimes(getCurrentTime);

    const deleteItems = (item) => {
        // server: "https://server9.mp3quran.net/abo_hashim/052.mp3"
        console.log(item.server);
        props.deleteFavFromSurabox(item.server);
        props.deleteItemFromPlaylist(item);
    }

    return (
        <PlayerContent suras={props.suras}>
            <Container>
                <div className='player_content'>
                    <div className='icons_controls_audio'>
                        <ul className='mb-0'>
                            <li onClick={randomChoice} className='mr-1 ml-1 mr-sm-2 ml-sm-2'>
                                <button className={props.suras.length === 0 ? 'disabled' : null} disabled={props.suras.length === 0 ? true : false}>
                                    {<FaRandom size={14} />}
                                </button>
                            </li>
                            <li onClick={(e) => skippedTime('-10')} className='mr-1 ml-1 mr-sm-2 ml-sm-2'>
                                <button>
                                    {<AiOutlineMinus size={20} />}
                                </button>
                            </li>
                            <li onClick={toggleAudio} className='main-btn mr-1 ml-1 mr-sm-2 ml-sm-2'>
                                <button>    
                                    {props.isPlaying ? <BiPlay size={30} style={{ paddingLeft: '5px' }} /> : <BiPause size={30} />}
                                </button>
                                </li>
                            <li onClick={(e) => skippedTime('+10')} className='mr-1 ml-1 mr-sm-2 ml-sm-2'>
                                <button>
                                    {<AiOutlinePlus size={20} />}
                                </button>
                            </li>
                        </ul>
                    </div>
                    <div className='progress_valume pl-2 pr-0 pr-md-2'>
                        <div className='progress_bar_controls'>
                            <span className='current-time'>{currentTimeAudio}</span>
                            <div className='full-progress' id='full-progress' onClick={scrub}>
                                <audio id='my-audio' ref={myAudioRef} src={props.suraLink} preload="metadata" />
                                <div className='short-progress' id='short-progress'></div>
                            </div>
                            <span className='full-time'>{fullTime}</span>
                        </div>
                        <div className='playlist_items d-none d-md-block'>
                            <AiFillHeart size={25} color='#f5b44b' />
                            <div className='playlist_list show'>
                                <div className='header d-flex-align-items-center'>
                                    <AiOutlineClose size={20} />
                                </div>
                                <ul className='list-unstyled sura_list'>
                                    {
                                    props.playlist && props.playlist.length > 0 
                                    ?  props.playlist.map((e, i) => (
                                        <Playlist
                                            key={e.server}
                                            server={e.server}
                                            number={e.number}
                                            name={e.name}
                                            rewaya={e.rewaya}
                                            index={i}
                                            suraName={e.suraName}
                                            deleteItem={(item) => deleteItems(item)}
                                        />
                                    ))
                                    : <div className='no_list text-center text-muted text-capitalize'>No Items Here... <CgTrashEmpty size={26} color='#6c757d' /></div>
                                    }
                                    {/* <li>
                                        <div className='play_btn d-flex align-items-center justify-content-center'>
                                            <PlayBtn size={24} color='#FFF' />
                                        </div>
                                        <div className='info d-flex align-items-center'>
                                            <div className='sura_number'>001</div>
                                            <div className='sura_desc'>
                                                <h6 className='sura_Name text-capitalize m-0'>al-fateha</h6>
                                                <span className='sura_info text-capitalize'>{cutSentance(`Abdelbari Al-Toubayti(Rewayat Hafs A'n Assem)`)}</span>
                                            </div>
                                            <div className='sura_delete' title='Delete Item'>
                                                <MdDelete size={24} />
                                            </div>
                                        </div>
                                    </li> */}
                                </ul>
                            </div>
                        </div>
                        <div className='volume-controll ml-1'>
                            <span className='volume-icon' title={`${value * 100}%`}>
                                {value > 0.70 && (<>
                                    <BsVolumeUp size={27} />
                                </>)}
                                {value <= 0.70 && value > 0.01 && (<>
                                    <BsVolumeDown size={27} />
                                </>)}
                                {value == 0.00 && (<>
                                    <BsVolumeMute size={27} />
                                </>)}
                            </span>
                            <div className='player-slider-parent'>
                                <input type='range' name='value' min='0' max='1' value={value} onChange={(e) => valueHanler(e)} step='.05' title={`${value * 100}%`} className='player-slider' />
                            </div>
                        </div>
                    </div>
                    <div className='sura_description text-center d-none d-md-block'>
                        <h6 className='text-capitalize mb-0 play_item_name'>{props.imamNameBeforeUpdate}</h6>
                        <span className='text-capitalize play_item_sura'>{props.suraName} </span>
                    </div>
                </div>
            </Container>
        </PlayerContent>
    )
});

const mapStateToProps = state => {
    return {
        imamName: state.getData.name,
        suraName: state.suraName,
        suraLink: state.suraLink,
        isPlaying: state.isPlaying,
        suras: state.suras,
        allSurasNames: state.allSurasNames,
        isImamPage: state.isImamPage,
        imamNameBeforeUpdate: state.imamNameBeforeUpdate,
        isRandom: state.isRandom,
        playlist: state.playlist
    }
}

const mapDispatchToProps = dispatch => ({
    togglePlay: () => dispatch(actions.playingToggle()),
    playingFalse: () => dispatch(actions.playingFalse()),
    playingTrue: () => dispatch(actions.playingTrue()),
    suraLinkRandom: (data) => dispatch(actions.suraLinkRandom(data)),
    changeName: (name) => dispatch(actions.changeName(name)),
    imamNameFunc: (name) => dispatch(actions.GetImamNameBeforeUpdate(name)),
    deleteItemFromPlaylist: (item) => dispatch(actions.removeItemFromPlayList(item)),
    deleteFavFromSurabox: (item) => dispatch(actions.deleteFavFromSurabox(item))
});

export default connect(mapStateToProps, mapDispatchToProps)(SongPlayer);
