        .rhap_time {
            color: ${props => props.theme === 'light' ? '#6d6d6d' : props.themeStyle.all.whiteColor};
        }
        .rhap_controls-section {
            .rhap_additional-controls {
                button[aria-label='Disable Loop'] {
