            .rhap_volume-controls {
                .rhap_volume-button {
                    color: rgba(57, 194, 201, .4);
                }
                .rhap_volume-bar-area {
                    .rhap_volume-bar {
                        background-color: rgba(57, 194, 201, .4);
                    }
                    .rhap_volume-indicator {
                        background-color: ${props => props.themeStyle.all.mainColor};
                    }
                }
            }
        }
    }
`;

