import React, { useCallback } from 'react';
import './imamBoxs.css';
import LetterSection from '../letters/letterSection';
import ShikhNames from '../shikhNames/shikhNames';
import { withRouter } from 'react-router-dom';
import { connect } from 'react-redux';
import * as actions from '../../store/action/getData';


const imamBoxs = React.memo(props => {

    const clicked = useCallback(link => {
        props.getData && props.imamName(props.getData.name);
        props.getDataFunc(link);
        props.getServer(link.Server);
        props.history.push(`/:lang/:${encodeURI(box.text.toLowerCase())}`);
    }, []);
    console.log(props)
    let allElementsInSection = [];
    const allboxs = props.site_language && props.site_language.complete_quran !== null && props.site_language.suras !== null && props.boxes.map(box => {
        if(props.search === '') {
            if(['select rewaya', 'all'].includes(props.chooseSelect)) {
                allElementsInSection.push (
                    <ShikhNames 
                        clicked={() => clicked(box)}
                        key={box.id}
                        link={box.Server}
                        text={box.name}
                        title={+box.count === 114 && props.site_language && props.site_language.complete_quran !== null && props.site_language.suras !== null ? props.site_language.complete_quran : `${+box.count} ${props.site_language.suras}`}
                        count={+box.count}
                        suras={box.suras}
                        rewaya={box.rewaya}
                    />
                );    
            } else if(props.chooseSelect.toLocaleLowerCase() === box.rewaya.toLocaleLowerCase()) {
                allElementsInSection.push (
                    <ShikhNames 
                        clicked={() => clicked(box)}
                        key={box.id}
                        link={box.Server}
                        text={box.name}
                        title={+box.count === 114 && props.site_language && props.site_language.complete_quran !== null && props.site_language.suras !== null ? props.site_language.complete_quran : `${+box.count} ${props.site_language.suras}`}
                        count={+box.count}
                        suras={box.suras}
                        rewaya={box.rewaya}
                    />
                );
            }
        } else if(box.name.toLocaleLowerCase().includes(props.search.toLocaleLowerCase())) {
            if(props.chooseSelect === 'select rewaya' || props.chooseSelect === 'all') {
                allElementsInSection.push (
                    <ShikhNames 
                        clicked={() => clicked(box)}
                        key={box.id}
                        link={box.Server}
                        text={box.name}
                        title={+box.count === 114 && props.site_language && props.site_language.complete_quran !== null && props.site_language.suras !== null ? props.site_language.complete_quran : `${+box.count} ${props.site_language.suras}`}
                        count={+box.count}
                        suras={box.suras}
                        rewaya={box.rewaya}
                    />
                );    
            } else if(props.chooseSelect.toLocaleLowerCase() === box.rewaya.toLocaleLowerCase()) {
                allElementsInSection.push (
                    <ShikhNames 
                        clicked={() => clicked(box)}
                        key={box.id}
                        link={box.Server}
                        text={box.name}
                        title={+box.count === 114 && props.site_language && props.site_language.complete_quran !== null && props.site_language.suras !== null ? props.site_language.complete_quran : `${+box.count} ${props.site_language.suras}`}
                        count={+box.count}
                        suras={box.suras}
                        rewaya={box.rewaya}
                    />
                );
            }
        }
    });

    return props.site_language && allElementsInSection.length > 0 ? (
        <div className='emam-section' id={`letter_${props.letter}`}>
            <LetterSection letter={props.letter} />
            <div className='emams-boxes'>
                {allElementsInSection}
            </div>
        </div>
    ) : null;
});

const mapStateToprops = state => ({
    getData: state.getData,
    site_language: state.site_language
});

const mapDispatchToProps = dispatch => {
    return {
        getDataFunc: (data) => dispatch(actions.getData(data)),
        getServer: (server) => dispatch(actions.getServer(server)),
        imamName: (name) => dispatch(actions.getImamNameBeforeUpdate(name))
    }
} 

export default connect(mapStateToprops, mapDispatchToProps)(withRouter(imamBoxs));
