import styled from 'styled-components';

export const SurahBoxList = styled.li`
    padding-top: 7px;
    padding-bottom: 7px;
    border-radius: 6px;
    background-color: #fff;
    box-shadow: 0 2px 8px 0 hsl(0deg 0% 64% / 19%);
    & .icon_name_number {
        flex-basis: 65%;
        @media (max-width: 350px) {
            flex-basis: 100%;
        }
    }
    & .surah-box-number {
        margin-left: 6px;
        margin-right: 6px;
    }
`;


