import React from 'react';
import { AiFillHome, AiFillHeart } from 'react-icons/ai';
import { FaBroadcastTower } from 'react-icons/fa';
import { AsideStyled } from './asideStyled';
import AsideLink from './asideList/asideLink';
import { connect } from 'react-redux';

const aside = (props) => {
    return (
        <AsideStyled theme={props.theme} themeStyle={props.themeStyle}>
            <ul className='mb-0'>
                <AsideLink text={props.language && props.language.lang === 'ar' ? 'الرئيسية' : 'Home'} link='/' exact scrollTopScreen={props.scrollTopScreen}>
                    <AiFillHome size={20} />
                </AsideLink>
                <AsideLink text={props.site_language && props.site_language.favorite} link='/favorite'>
                    <AiFillHeart size={20} />
                </AsideLink>
                <AsideLink text={props.site_language && props.site_language.radio} link='/live'>
                    <FaBroadcastTower size={20} />
                </AsideLink>
            </ul>
        </AsideStyled>
    )
}

const mapStateToProps = state => ({
    theme: state.theme,
    themeStyle: state.themeStyle,
    language: state.language,
    site_language: state.site_language
});
export default connect(mapStateToProps)(aside);
