import * as actionType from './actionTypes';

export const closeAside = () => ({type: actionType.CLOSE_ASIDE});
export const openAside = () => ({type: actionType.OPEN_ASIDE});
export const showPlayer = () => ({type: actionType.SHOW_PLAYER});
