import styled from 'styled-components';

export const Surahbox = styled.div`
    padding-top: 7px;
    padding-bottom: 7px;
    border-radius: 6px;
    background-color: ${props => props.themeStyle[props.theme].secondaryColor};
    box-shadow: ${props => props.themeStyle[props.theme].boxShadow};
    display: flex;
    justify-content: space-between;
    flex-wrap: wrap;
    .icon_name_number_section {
        flex-basis: 65%;
        @media (max-width: 350px) {
            flex-basis: 100%;
        }
        .surah-box-number {
            margin-left: 6px;
            margin-right: 6px;
            color: ${props => props.theme === 'light' ? props.themeStyle.all.mutedColor : props.themeStyle.all.secondaryColor};
        }
        .surah-box-name {
            color: ${props => props.themeStyle[props.theme].textNormal};
            white-space: nowrap;
        }
        .immam_name {
            font-size: 13px;
            letter-spacing: 0.3px;
            padding: 0 6px;
            color: ${props => props.theme === 'light' ? props.themeStyle.all.mutedColor : props.themeStyle.all.yellowColor};
        }
    }
    .surah-box-imam-page-info {
        flex-basis: 30%;
        justify-content: space-between;
        @media (max-width: 350px) {
            flex-basis: 100%;
            justify-content: flex-end;
        }
        .list {
            cursor: pointer;
            border-radius: 6px;
            transition: all 0.3s ease-in-out;
            &.hide_on_hover {
                opacity: 0;
                &:hover button {
                    color: ${props => props.themeStyle.all.whiteColor};
                }
            }
            &.show_on_hover {
                opacity: 1;
            }
            button {
                color: ${props => props.theme === 'light' ? props.themeStyle.all.blackColor : props.themeStyle.all.whiteColor};
                padding: 2px 5px;
                &.delete_item {
                    color: ${props => props.themeStyle.all.redColor};
                }
            }
            &.heart {
                color: ${props => props.themeStyle.all.blackColor};
                &.full_heart_btn {
                    color: ${props => props.themeStyle.all.yellowColor};
                }
            }
        }
    }
`;
