import React from 'react';
import './layout.css';
import Navigation from '../Navagations/Navigation';
import Asdie from '../aside/aside';
import HeaderTitle from '../headerTitle/headerTitle';
import Container from 'react-bootstrap/Container';
import Row from 'react-bootstrap/Row';
import Col from 'react-bootstrap/Col';
import PlayerSong from '../songPlayer/songPlayer';
import { connect } from 'react-redux';
import Sidebar from '../Navagations/sidebar/sidebar';

const layout = props => (
    <>
        <Navigation />
        <HeaderTitle />
        <Sidebar />
        <main>
            <Container>
                <Row>
                    <Col md={4} lg={3} className='d-none d-md-block'>
                        <Asdie />
                    </Col>
                    <Col md={8} lg={9}>
                        {props.children}
                    </Col>
                </Row>
            </Container>
            { props.isShow && <PlayerSong /> }
        </main>
    </>
);

const mapStateToProps = state => ({
    isShow: state.showPlayer
});

export default connect(mapStateToProps)(layout);
