import React from 'react';
// import './aside.css';
import { AiFillHome, AiFillHeart } from 'react-icons/ai';
import { FaBroadcastTower } from 'react-icons/fa';
import { AsideStyled } from './asideStyled';
import AsideLink from './asideList/asideLink';
import { connect } from 'react-redux';


const aside = (props) => {
    return (
        <AsideStyled theme={props.theme} themeStyle={props.themeStyle}>
            <ul className='mb-0'>
                <AsideLink text='Home' link='/' exact scrollTopScreen={props.scrollTopScreen}>
                    <AiFillHome size={20} />
                </AsideLink>
                <AsideLink text='favorite' link='/favorite'>
                    <AiFillHeart size={20} />
                </AsideLink>
                <AsideLink text='live radio' link='/live'>
                    <FaBroadcastTower size={20} />
                </AsideLink>
            </ul>
        </AsideStyled>
    )
}

const mapStateToProps = state => ({
    theme: state.theme,
    themeStyle: state.themeStyle
});
export default connect(mapStateToProps)(aside);
