import React, { useState, useEffect, useMemo } from 'react'
import { LogInStyled, EmailStyled, PasswordStyled } from './logInStyled';
import { connect } from 'react-redux';
import Form from 'react-bootstrap/Form';
import FormControl from 'react-bootstrap/FormControl';
import FormText from 'react-bootstrap/FormText';
import Button from 'react-bootstrap/Button';
import { Link } from 'react-router-dom';

const Login = props => {

    const [email, setEmail] = useState('');
    const [password, setPassword] = useState('');
    // const [emailValid, setEmailValid] = useState(true);
    // const [passwordValid, setPasswordValid] = useState(true);
    const [successForm, setSuccessForm] = useState(false);

    const submitHandler = (e) => {
        e.preventDefault();
        console.log('Submit');
    }

    const ValidateEmail = useMemo(() => {
        const re = /^(([^<>()[\]\\.,;:\s@"]+(\.[^<>()[\]\\.,;:\s@"]+)*)|(".+"))@((\[[0-9]{1,3}\.[0-9]{1,3}\.[0-9]{1,3}\.[0-9]{1,3}\])|(([a-zA-Z\-0-9]+\.)+[a-zA-Z]{2,}))$/;
        return re.test(String(email).toLowerCase());
    }, [email]);

    const Validatepassword = useMemo(() => password.length > 4, [password]);

    useEffect(() => {
        if(email.length === '' && password.length === '') {
            setSuccessForm(false);
        } else if(email.length > 0 && password.length > 0) {
            if(ValidateEmail && Validatepassword) {
                setSuccessForm(true);
            } else {
                setSuccessForm(false);
            }
        }

    }, [email, password, ValidateEmail, Validatepassword]);

    const changeEmailHandler = (e) => {
        setEmail(e.target.value);
        // setEmailValid(ValidateEmail);
    }
    
    const changePasswordHandler = (e) => {
        setPassword(e.target.value);
        // setPasswordValid(Validatepassword);
    }

    console.log('successForm');

    return (
        <LogInStyled themeStyle={props.themeStyle} disabled={successForm}>
            <h4 className='title text-capitalize mb-3'>log in</h4>
            <div className='login_social text-center mb-4'>
                <Button className='social_btn face text-capitalize d-block mb-2 w-100 border-0'>Facebook</Button>
                <Button className='social_btn google text-capitalize d-block mb-3 w-100 border-0'>Google</Button>
                <span className='small_message'> or log in with</span>
            </div>
            <div className='my_form'>
                <Form onSubmit={submitHandler}>
                    <EmailStyled emailValid={ValidateEmail} themeStyle={props.themeStyle}>
                        <FormControl type='email' placeholder='Email' value={email} onChange={e => changeEmailHandler(e)} />
                    </EmailStyled>
                    <PasswordStyled passwordValid={Validatepassword} themeStyle={props.themeStyle}>
                        {!Validatepassword && <FormText className='error_msg pass text-capitalize mb-1'>at leats 5 Characeters</FormText>}
                        <FormControl type='password' placeholder='Password' value={password} onChange={e => changePasswordHandler(e)} />
                    </PasswordStyled>
                    <Button type='submit' disabled={!successForm} className='submit text-capitalize d-block border-0 mt-4 w-100'>login</Button>
                </Form>
            </div>
            <div className='dont_have_account mt-5 text-center'>
                <p>Don't have account <Link to='/signup'>Sign up</Link></p>
            </div>
        </LogInStyled>
    )
}

const mapStateToProps = state => ({
    themeStyle: state.theme.themeStyle
})

export default connect(mapStateToProps)(Login);
