import styled from 'styled-components';

export const AsideLink = styled.div`
    & > a {
        color: ${props => theme === 'light' ?  '#1d1f21' : props.themeStyle.all.whiteColor};
    }
    & > a:before {
        content: '';
        position: absolute;
        top: 0;
        width: 2px;
        height: 100%;
        z-index: -1;
    }
    & > a.active:before,
    & > a:hover:before
    {
        content: '';
        ${props => props.site_lang === 'eng' ? 'left' : 'right'}: -2px;
        background-color: ${props => props.themeStyle.all.mainColor};
    }
    & > a.active {
        color: ${props => props.themeStyle.all.mainColor};
    }
    
    & > a.active .aside-list-logo {
        color: ${props => props.themeStyle.all.mainColor};
    }
`;

