import styled from 'styled-components';

export const PlayerContent = styled.div`
    position: fixed;
    bottom: 0;
    left: 0;right: 0;
    width: 100%;
    height: 50px;
    background-color: #FFF;
    box-shadow: 0 -2px 8px 2px hsl(0deg 0% 64% / 19%);
    z-index: 999;
    @media (max-width: 576px) {
        height: 70px;
    }
    & > .container {
        height: 100%;
    }
`;
