import React from 'react';
import PlayBtn from '../../components/playBtn/playBtn';
import LinkBtn from '../../components/linkBtn/linkBtn';
import DotBtn from '../../components/DotBtn/DotBtn';
import { MdDelete } from 'react-icons/md';

const playlist = props => {

    const deleteFunc = () => {
        console.log('deleteFunc');
    }


    const playbtn = (link, number, suraName, imamName) => {
        console.log(imamName);
        console.log('Play Func');
    }

    return (
        <li className='surah-box-imam-page pr-1 pl-1 pr-sm-3 pl-sm-3' title='ahmed salem'>
            <div className='icon_name_number_section d-flex flex-column'>
                <div className='icon_name_number d-flex align-items-center align-items-center'>
                    <PlayBtn size={33} color='#F5B44B' clicked={() => playbtn(props.server, props.number, props.suraName, props.name)} />
                    <div className='text d-flex flex-column' onClick={() => playbtn()}>
                        <div className='d-flex surah-box-number-number-text'>
                            <div className='surah-box-number text-muted'>{props.number}</div>
                            <div className='surah-box-name text-capitalize'>{props.suraName}</div>
                        </div>
                        <span className='immam_name text-capitalize text-muted'>{props.name}</span>
                    </div>
                </div>
            </div>
            <div className='surah-box-imam-page-info d-flex align-items-center'>
                <div className='list hide_on_hover m-0' title='copy link' onClick={() => navigator.clipboard.writeText(props.server)}>
                    <LinkBtn />
                </div>
                <div className='list hide_on_hover m-0' title='delete' onClick={deleteFunc}>
                    <button className='btn'>
                        <MdDelete size={24} />
                    </button>
                </div>
                <div className='list show_on_hover m-0'>
                    <DotBtn />
                </div>
            </div>
        </li>
    )
}

export default playlist
