import React from 'react';
import { connect } from 'react-redux';
import { ShikhNames } from './shikhNameStyles';

const shikhNames = props => {
    return (
        <ShikhNames className='shikh-names justify-content-between' title={props.title} onClick={props.clicked} theme={props.theme} themeStyle={props.themeStyle}>
            <div className='text_name'>
                <h6>{props.text}</h6>
            </div>
            <div className='info d-flex align-items-center'>
                <div className='suras_count text-capitalize'>{props.themeStyle.suras}</div>
                {/* <div className='count'>{props.title}</div> */}
            </div>
        </ShikhNames>
    )
};

const mapStateToProps = state => ({
    theme: state.theme,
    themeStyle: state.themeStyle
});

export default connect(mapStateToProps)(shikhNames);
