import React, { useCallback } from 'react';
import './imamBoxs.css';
import LetterSection from '../letters/letterSection';
import ShikhNames from '../shikhNames/shikhNames';
import { withRouter } from 'react-router-dom';
import { connect } from 'react-redux';
import * as actions from '../../store/action/getData';


const imamBoxs = React.memo(props => {
    const clicked = useCallback(link => {

        props.getData && props.imamName(props.getData.name);
        props.getDataFunc(link);
        props.getServer(link.Server);
        props.history.push('/imamPage')
    }, []);

    // const allboxs = props.boxes.map(box => <ShikhNames 
    //     clicked={() => clicked(box)}
    //     key={box.id}
    //     link={box.Server}
    //     text={box.name}
    //     title={+box.count === 114 ? 'All Quran' : `${+box.count} Sura`}
    //     count={+box.count}
    //     suras={box.suras}
    //     rewaya={box.rewaya}
    // />)
    let r = [];
    const allboxs = props.boxes.map(box => {
        if(props.search === '' || props.search === 'all') {
            return (
                <ShikhNames 
                    clicked={() => clicked(box)}
                    key={box.id}
                    link={box.Server}
                    text={box.name}
                    title={+box.count === 114 ? 'All Quran' : `${+box.count} Sura`}
                    count={+box.count}
                    suras={box.suras}
                    rewaya={box.rewaya}
                />
            );
        } else if(box.name.toLocaleLowerCase().includes(props.search.toLocaleLowerCase())) {
            return (
                <ShikhNames 
                    clicked={() => clicked(box)}
                    key={box.id}
                    link={box.Server}
                    text={box.name}
                    title={+box.count === 114 ? 'All Quran' : `${+box.count} Sura`}
                    count={+box.count}
                    suras={box.suras}
                    rewaya={box.rewaya}
                />
            );
        }        
        // return (
        //     <ShikhNames 
        //         clicked={() => clicked(box)}
        //         key={box.id}
        //         link={box.Server}
        //         text={box.name}
        //         title={+box.count === 114 ? 'All Quran' : `${+box.count} Sura`}
        //         count={+box.count}
        //         suras={box.suras}
        //         rewaya={box.rewaya}
        //     />
        // );
    })

    console.log(props.search);
    return (
        <div className='emam-section' id={`letter_${props.letter}`}>
            <LetterSection letter={props.letter} />
            <div className='emams-boxes'>
                {allboxs}
            </div>
        </div>
    )
});

const mapStateToprops = state => ({
    getData: state.getData
});

const mapDispatchToProps = dispatch => {
    return {
        getDataFunc: (data) => dispatch(actions.getData(data)),
        getServer: (server) => dispatch(actions.getServer(server)),
        imamName: (name) => dispatch(actions.getImamNameBeforeUpdate(name))
    }
} 

export default connect(mapStateToprops, mapDispatchToProps)(withRouter(imamBoxs));
