import React from 'react';
import './letter.css';
import { connect } from 'react-redux';
import { Letter } from './lettersStyled';

// const letter = props => {
//     return (
//         <a href={props.id} className="fancy-button bg-gradient1">
//             <span>{props.text}</span>
//         </a>
//     )
// }

const letter = props => {
    return (
        <Letter>
            <span>{props.text}</span>
        </Letter>
    )
}

const mapStateToProps = state => ({
    theme: state.theme,
    themeStyle: state.themeStyle
});

export default connect(mapStateToProps)(letter);
