import React, { useReducer, useCallback, useState, useEffect } from 'react';
import './surahBox.css';
import PlayBtn from '../playBtn/playBtn';
import HeartBtn from '../heartBox/heartBox';
import LinkBtn from '../linkBtn/linkBtn';
import PlusListBtn from '../plusListIcon/plusListIcon';
import DotBtn from '../DotBtn/DotBtn';
import ToastComp from '../toastCompo/toastCompo';
import { connect } from 'react-redux';
import * as actions from '../../store/action/getData';

const initialState = (state, action) => {
    switch(action.type) {
        case 'isToastOpen':
            return {...state, isToastOpen: true, msg: action.msg, isFavorite: action.isFavorite}
        case 'isToastClose':
            return {...state, isToastOpen: false, msg: {}, isFavorite: state.isFavorite}
        case 'isFavorite':
            return {...state, isFavorite: action.isFavorite}
        case 'Msg':
            return {...state, msg: action.msg}
        default: 
        return state;
    }
}

const SurahBox = props => {
    const [surahState, dispatch] = useReducer(initialState, {
        isToastOpen: false,
        isFavorite: props.isFavor,
        msg: {}
    });

    const [isFav, setIsFav] = useState(props.isFavor);

    const toggleFav = () => {
        if(isFav) {
            props.playlist.map(list => {
                if(list.server === `${props.allData.Server}/${props.number}.mp3` && list.number === props.number && list.suraName === props.name) {
                    props.deleteItemFromPlaylist({server: list.server});
                }
            });
        }
        setIsFav(!isFav);
    };

    // if(props.deleteFav) {
        // console.log('https://server9.mp3quran.net/abo_hashim/050.mp3' === 'https://server9.mp3quran.net/abo_hashim/050.mp3');
        // console.log(props.number);
        // console.log(`${props.server}/${props.number}.mp3`.length);
        // console.log(props.deleteFav.length);
    //     console.log(`${props.server}/${props.number}.mp3` + ' %% ' + props.deleteFav);
    //     console.log(`${props.server}/${props.number}.mp3` === props.deleteFav);
    // }

    useEffect(() => {
        if(`${props.server}/${props.number}.mp3` === props.deleteFav) {
            setIsFav(false);
            console.log(props.deleteFav);
            console.log('963852741');
        }
    }, [props.deleteFav]);

    // if(`${props.server}/${props.number}.mp3` === props.deleteFav) {
    //     setIsFav(false);
    //     console.log(props.deleteFav);
    // }

    // console.log(props.deleteFav);
    // console.log(`${props.server}/${props.allData.name}/${props.number}.mp3`);
    // if(props.deleteFav === `${props.server}/${props.allData.name}/${props.number}.mp3`) {
    //     setIsFav(false);
    //     console.log('HHHHHHHHHHHHH')
    // }

    // {server: props.server}

    /* Props allData*/
    // Server: "https://server9.mp3quran.net/abo_hashim"
    // count: "8"
    // id: "73"
    // letter: "A"
    // name: "Ali Abo-Hashim"
    // rewaya: "Rewayat Hafs A'n Assem"
    // suras: "50,51,52,53,54,55,56,57"

    /* playListFav */
    // isFavorite(pin):true
    // server(pin):"https://server9.mp3quran.net/abo_hashim/050.mp3"
    // number(pin):"050"
    // name(pin):"Ali Abo-Hashim"
    // rewaya(pin):"Rewayat Hafs A'n Assem"
    // suraName(pin):"Qaf"

    // console.log(props.allData)

    const setTime = () => setTimeout(() => {closeToast()}, 3000);

    const openToast = useCallback((msg) => {
        dispatch({type: 'isToastOpen', msg: {...msg}, isFavorite: msg.isFavorite });
    }, []);

    const closeToast = useCallback(() => {
        clearTimeout(setTime);
        dispatch({type: 'isToastClose', msg: {}, isFavorite: surahState.isFavorite});
    }, []);

    const cpiedFunc = useCallback(msg => {
        clearTimeout(setTime);
        openToast({title: 'Copied', myMsg: msg, myClass: 'added_item', isFavorite: surahState.isFavorite});
        navigator.clipboard.writeText(props.copyText);
    }, []);

    const cutSentance = n => props.name && props.name.length > 11 ? `${props.name.slice(0, n)}...` : props.name;

    const favFunc = () => {
        clearTimeout(setTime);
        if(surahState.isFavorite) {
            openToast({title: 'Remove', myMsg: 'the item was removed from favorites', myClass:'remove_item', isFavorite: surahState.isFavorite});
            dispatch({type: 'isFavorite', msg: 'the item was removed from favorites', isFavorite: !surahState.isFavorite });
        } else {
            openToast({title: 'Added', myMsg: 'the item was added to favorites', myClass: 'added_item', isFavorite: surahState.isFavorite});
            dispatch({type: 'isFavorite', msg: 'the item was removed from favorites', isFavorite: !surahState.isFavorite });
        }
    }

    // console.log(props)

    surahState.isToastOpen ? setTime() : clearTimeout(setTime);
    
    const addToFavList = (name) => {
        favFunc();
        props.addFav({isFavorite: !surahState.isFavorite, allData: props.allData });
    }
    
    return (
        <>
            {surahState.isToastOpen && <ToastComp msg={surahState.msg} />}
            <li className='surah-box-imam-page d-flex justify-content-between pr-1 pl-1 pr-sm-3 pl-sm-3' title={props.name}>
                <div className='icon_name_number d-flex align-items-center' onClick={props.clicked}>
                    <PlayBtn size={26} color='#F5B44B' clicked={props.isPlayToggle} />
                    <div className='surah-box-number text-muted' onClick={props.isPlayToggle}>{props.number}</div>
                    <div className='surah-box-name text-capitalize' onClick={props.isPlayToggle}>{props.name}</div>
                    {/* <div className='surah-box-name text-capitalize' onClick={props.isPlayToggle}>{cutSentance(25)}</div> */}
                </div>
                <div className='surah-box-imam-page-info d-flex align-items-center justify-content-between'>
                    <div className='list hide_on_hover m-0' title='copy link' onClick={() => cpiedFunc('The link is copied')}>
                        <LinkBtn />
                    </div>
                    <div className='list hide_on_hover heart m-0' title='add to favorite' onClick={addToFavList} >
                        <HeartBtn size={22} bgColor='transparent' server={props.server} number={props.number} isFav={isFav} toggleFav={toggleFav} />
                    </div>
                    <div className='list show_on_hover m-0'>
                        <DotBtn />
                    </div>
                </div>
            </li>
        </>
    )
};

const mapStateToProps = state => ({
    playlist: state.playlist,
    deleteFav: state.deleteFav
});

const mapDispatchToProps = dispatch => ({
    deleteItemFromPlaylist: (item) => dispatch(actions.removeItemFromPlayList(item))
});

export default connect(mapStateToProps, mapDispatchToProps)(SurahBox);
