import React from 'react';
import './aside.css';
import AsideList from './asideList/asideList';
import { AiFillHome, AiFillHeart } from 'react-icons/ai';
import { ImSearch } from 'react-icons/im';
import { VscLibrary } from 'react-icons/vsc';
import { Aside } from './asideStyled';



const aside = (props) => {
    return (
        <Aside>
            <ul className='mb-0'>
                <AsideList text='Home' link='/' exact scrollTopScreen={props.scrollTopScreen}>
                    <AiFillHome size={20} />
                </AsideList>
                <AsideList text='Search' link='/search'>
                    <ImSearch size={20} />
                </AsideList>
                <AsideList text='Your Library' link='/library'>
                    <VscLibrary size={20} />
                </AsideList>
                <AsideList text='favorite' link='/favorite'>
                    <AiFillHeart size={20} />
                </AsideList>
            </ul>
        </Aside>
    )
}

export default aside
