import React from 'react';
import { BiPlay } from 'react-icons/bi';
import './playBtn.css';

const PlayBox = props => {
    const classes = ['play-btn', props.classes]
    return (
        <div className={classes.join(' ')} title='Play icon' onClick={props.clicked}>
            <BiPlay size={props.size} color={props.color} />
        </div>
    )
};

export default PlayBox;
