import React from 'react';
import { AiOutlineHeart, AiFillHeart } from 'react-icons/ai';
import { HeartIconBtn } from './heartStyled';
import { connect } from 'react-redux';

const HeartBox = props => {
    return (
        <HeartIconBtn onClick={props.toggleFav} bgColor={props.themeStyle.all.yellowColor}>
            {props.isFav 
                ? <span className='full_heart_btn'><AiFillHeart size={props.size} color={props.colorOne} /></span> 
                : <span className='empty_heart_btn'><AiOutlineHeart size={props.size} color={props.colorTwo} /></span> }
        </HeartIconBtn>
    )
};

const mapStateToProps = state => ({
    state: state.themeStyle
});

export default connect(mapStateToProps)(HeartBox);
