import React from 'react';
import { connect } from 'react-redux';
import { ShikhNames } from './shikhNameStyles';

const shikhNames = props => {
    return (
        <ShikhNames className='shikh-names' title={props.title} onClick={props.clicked} theme={props.theme} themeStyle={props.themeStyle}>
            <h6>{props.text}</h6>
        </ShikhNames>
    )
};

const mapStateToProps = state => ({
    theme: state.theme,
    themeStyle: state.themeStyle
});

export default connect(mapStateToProps)(shikhNames);
