import styled from 'styled-components';
export const PageNotFound = styled.div`
    height: 600px;
    background-color: ${props => props.themeStyle[props.theme].bg};
    border-radius: 6px;
    padding: 60px;
    text-align: center;
    .text_not_found {
        margin-top: 50px;
        font-size: 30px;
        font-weight: bold;
        color: ${props => props.themeStyle[props.theme].textNormal};
        @media (max-width: 676px) {
            font-size: 22px;
        }
    }
`;
