const mapStateToProps = state => ({
    getData: state.getData,
    loading: state.loading,
    letters: state.letters,
    lettersIndex: state.lettersIndex,
    allElementsImams: state.allElementsImams,
    theme: state.theme,
    themeStyle: state.themeStyle,
    language: state.language,
    site_language: state.site_language
});

const mapDispatchToProps = dispatch => ({
    startMain: () => dispatch(actions.startMain()),
    successMain: (letters, lettersIndex, allEllemnt) => dispatch(actions.successMain(letters, lettersIndex, allEllemnt)),
    failRequest: () => dispatch(actions.failRequest()),
    emptyFavFromSurabox: () => dispatch(actions.emptyFavFromSurabox()),

    getDataFunc: (data) => dispatch(actions.getData(data)),
    getServer: (server) => dispatch(actions.getServer(server)),
    imamName: (name) => dispatch(actions.getImamNameBeforeUpdate(name)),

    isFavoritePage: (value) => dispatch(actions.isFavoritePage(value)),
    isRadioPage: (value) => dispatch(actions.isRadioPage(value))
})

export default connect(mapStateToProps, mapDispatchToProps)(withErrorHandler(Main, axios));
