import React, {Component, Fragment} from 'react';
import Modal from 'react-bootstrap/Modal';
import { connect } from 'react-redux';
import * as actions from '../../store/action/getData';

const WithErrorHandler = (Wrapper, axios) => {
    return class extends Component {
        // state = {error: props.error}
        
        componentDidMount() {
            const myReq = axios.interceptors.request.use(res => {
                // this.setState({error: null});
                props.getError(null);
                return res;
            });
            const myRes = axios.interceptors.response.use(res => res, error => {
                props.getError(error);
                // this.setState({error: error});
            });
        }

        componentWillUnmount() {
            axios.interceptors.request.eject(this.myReq);
            axios.interceptors.response.eject(this.myRes);
        }

        render() {
            return (
                <Fragment>
                    <Modal
                        size="sm"
                        show={this.state.error}
                        // onHide={() => this.setState({error: null})}
                        onHide={() => props.getError(null)}
                        aria-labelledby="example-modal-sizes-title-sm" >
                        <Modal.Header closeButton>
                            <Modal.Title id="example-modal-sizes-title-sm">
                                Error Message
                            </Modal.Title>
                        </Modal.Header>
                        <Modal.Body>
                            {/* {this.state.error && this.state.error.message} */}
                            {props.error && props.error.message}
                        </Modal.Body>
                    </Modal>
                    <Wrapper {...this.props} />
                </Fragment>
            )
        }
    }
}

const mapStateToProps = state => ({
    error: state.error
});

const mapDispatchToProps = dispatch => ({
    getError: (error) => dispatch(actions.getError(error))
});

export default connect(mapStateToProps, mapDispatchToProps)(WithErrorHandler);
