import styled from 'styled-components';

export const SurahBoxList = styled.li`
    padding-top: 7px;
    padding-bottom: 7px;
    border-radius: 6px;
    // background-color: ${props => props.themeStyle[props.theme].secondaryColor};
    // box-shadow: ${props => props.themeStyle[props.theme].boxShadow};
    & .icon_name_number {
        flex-basis: 65%;
        @media (max-width: 350px) {
            flex-basis: 100%;
        }
        & .play-btn {
            color: ${props => props.themeStyle.all.yellowColor};
        }
        & .surah-box-number {
            margin-left: 6px;
            margin-right: 6px;
            color: ${props => props.themeStyle.all.mutedColor}
        }
        & .surah-box-name {
            color: #1d1f21;
            white-space: nowrap;
        }
    }
    & .surah-box-imam-page-info {
        flex-basis: 35%;
        justify-content: space-between;
        @media (max-width: 350px) {
            flex-basis: 100%;
            justify-content: flex-end;
        }
        .list {
            cursor: pointer;
            border-radius: 6px;
            transition: all 0.3s ease-in-out;
            button {
                color: #1d1f21;
                padding: 2px 5px;
            }
            &.hide_on_hover {
                opacity: 0;
            }
            &.heart {
                color: #1d1f21;
                & .full_heart_btn {
                    color: #f5b44b;
                }
            }
            &.show_on_hover {
                opacity: 1;
            }
        }
    }
    &:hover .list.hide_on_hover {
        opacity: 1;
        &:hover {
            background-color: #14eeaa;
            svg {
                color: #fff;
            }
            button {
                color: #fff;
            }
        }
    }
    &:hover .list.show_on_hover {
        opacity: 0;
    }
`;
