import React from 'react';
import PlayBtn from '../playBtn/playBtn';
import { MdDelete } from 'react-icons/md';
import './playlist.css';

const playlist = props => {
    const cutSentance = n => n.length >= 45 ? `${n.slice(0, 45)}...` : n;
    // console.log(props)
    return (
        <li className='list'>
            <div className='play_btn d-flex align-items-center justify-content-center' onClick={() => props.clicked(props.server, props.number, `${props.name} (${props.rewaya})`)}>
                <PlayBtn size={24} color='#FFF' />
            </div>
            <div className='info d-flex align-items-center'>
                <div className='sura_number'>{props.number}</div>
                <div className='sura_desc'>
                    <h6 className='sura_Name text-capitalize m-0'>{props.suraName}</h6>
                    <span className='sura_info text-capitalize'>{cutSentance(`${props.name}(${props.rewaya})`)}</span>
                </div>
                <div className='sura_delete' title='Delete Item' onClick={() => props.deleteItem({server: props.server})}>
                    <MdDelete size={24} />
                </div>
            </div>
        </li>
    )
}

export default playlist
