import React, { lazy, Suspense, useEffect, useState } from 'react';
import './App.css';
import 'bootstrap/dist/css/bootstrap.min.css';
import { Route, Switch } from 'react-router-dom';
import Layout from '../components/layout/layout';
import Loading from '../components/UI/Loader/loader';
import {connect} from 'react-redux';
import * as actions from '../store/action/getData';
import axios from '../axiosBase/createAxios';
import withErrorHandler from '../hoc/withErrorHandler/withErrorHandler';

const AsyncMain = lazy(() => import('../containers/Home'));
const AsyncImamPage = lazy(() => import('../components/imamPage/ImamPage'));
const AsyncFavorite = lazy(() => import('../containers/Favorite/index'));
const AsyncRadios = lazy(() => import('../containers/Radios/index'));

const App = props => {

  // const [playlistStorage, setPlaylistStorage] = useState(JSON.parse(localStorage.getItem('playlist')) || []);
  // const [themeStorageValue, setThemeStorageValue] = useState(JSON.parse(localStorage.getItem('isThemeIsDark')) || false);
  // const [storageLangauage, setStorageLangauage] = useState(JSON.parse(localStorage.getItem('language')) || 'en');

  const playlistStorage = useState(JSON.parse(localStorage.getItem('playlist')) || [])[0];
  const themeStorageValue = useState(JSON.parse(localStorage.getItem('isThemeIsDark')) || false)[0];
  const storageLangauage = useState(JSON.parse(localStorage.getItem('language')) || 'en')[0];

  useEffect(() => {
    axios.get(`translations?languages=${props.language.lang}`)
    .then(res => {
      props.siteLanguage(res.data[props.language.lang]);
    })
    .catch(error => error);
  }, [props.language.lang]);

  useEffect(() => {
    if(playlistStorage) {
      props.playlistStorage(playlistStorage);
    }
    if(themeStorageValue) {
      props.toggleTheme(themeStorageValue);
    }

    props.changeLanguage(storageLangauage);

    document.getElementsByTagName('html')[0].setAttribute('lang', storageLangauage);

    if(['ar', 'ur', 'ug', 'fa'].includes(storageLangauage)) {
      document.getElementsByTagName('html')[0].setAttribute('dir', 'rtl');
    } else {
      document.getElementsByTagName('html')[0].setAttribute('dir', 'ltr');
    }
  }, [playlistStorage, themeStorageValue, storageLangauage]);

  useEffect(() => {
    console.log(playlistStorage)
    localStorage.setItem('playlist', JSON.stringify(props.playlist));
  }, [props.playlist]);

  let routes = (
    <Switch>
      <Route path='/' render={props => <AsyncMain {...props} />} exact />
      <Route path='/reciter/:immam_Name' render={props => <AsyncImamPage {...props} />} />
      <Route path='/favorite'  render={props => <AsyncFavorite {...props} />} />
      <Route path='/radios' render={props => <AsyncRadios {...props} />} />
    </Switch>
  );

  return props.siteLanguage ? (
    <div className="App">
      <Layout>
        <Suspense fallback={<Loading />}>{routes}</Suspense>
      </Layout>
    </div>
  ) : null;
};

const mapStateToProps = state => ({
  playlist: state.playlist,
  isThemeDark: state.isThemeDark,
  language: state.language,
  siteLanguage: state.siteLanguage
});

const mapDispatchToProps = dispatch => ({
  playlistStorage: (items) => dispatch(actions.playlistStorage(items)),
  toggleTheme: (items) => dispatch(actions.toggleTheme(items)),
  siteLanguage: (lang) => dispatch(actions.siteLanguage(lang)),
  changeLanguage: (lang) => dispatch(actions.changeLanguage(lang))
});

export default connect(mapStateToProps, mapDispatchToProps)(withErrorHandler(App, axios));
