const ImamPage = props => {
        
    const [state, dispatch] = useReducer(myReducer, initialState);
    const [search, setSearch] = useState('');
    const [filterSearch, setFilterSearch] = useState();

    useEffect(() => {
        props.startRequest();
        axios.get(`/translations?languages=${props.language.lang}`)
        .then(res => {
            dispatch({type: 'surahNumbers', numbers: props.suras});
            props.successRequest();
            let arrayNames = [];
            if(props.suras) {
                props.suras.map(num => arrayNames.push(res.data[props.language.lang][`sura${num}`]));
            }
            dispatch({type: 'surahNames', names: arrayNames})
            props.allSuras(arrayNames)
            console.log('Response');
        })
        .catch(error => {
            props.failRequest(error)
        });
    }, [props.language.lang]);

    useEffect(() => {
        props.server && state.suraNames && state.suraNumbers && state.suraNumbers.length > 0 && state.suraNumbers && suraLoop();
    }, [props.server, state.suraNames, state.suraNumbers, state.suraNumbers]);

    useEffect(() => {
        let array2 = [];
        state.allSurasBoxs.filter(sura => {
            if(search === '') {
                array2 = [state.allSurasBoxs];
                setFilterSearch(array2);
            } else if(sura.props.name.toLocaleLowerCase().includes(search.toLocaleLowerCase())) {
                console.log(sura)
                array2.push(
                    <SurahBox
                    key={`${props.server}/${sura.props.name}/${sura.props.number}/${sura.props.isFavor}`}
                    name={sura.props.name}
                    server={sura.props.server}
                    number={sura.props.number}
                    clicked={() => clickedHandler(sura.props.number, sura.props.name)}
                    addFav={(data) => addFavFun(data, sura.props.number, sura.props.name)}
                    isFavor={sura.props.isFavor}
                    allData={sura.props.allData}
                    copyText={sura.props.copyText}
                />);
            }
            return array2;
        });
        setFilterSearch(array2);
    }, [search, props.getData]);

    useEffect(() => {
        window.scrollTo(0, 0)
    }, []);

    console.log('Immam Page ========>>>>>>');

    const clickedHandler = (index, suraName) => {
        let fixNum = index.length === 1 ? `00${index}` : index.length === 2 ? `0${index}` : index;
        props.getSurah_Number_Name(fixNum, suraName);
        props.suraLink(`${props.server}/${fixNum}.mp3`);
        props.imamName(props.getData.name);
        props.isFromFavouritePlaylist(false);
        props.playingFalse();
        props.showPlayer();
        props.getSuras();
    }

    const addFavFun = (data, num, suraName) => {
        let setData = {...data, number: num};
        const collectData = {allDataPlaylist: data, isFavorite: setData.isFavorite, server: `${setData.allData.Server}/${setData.number}.mp3`, number: setData.number, name: setData.allData.name, rewaya: setData.allData.rewaya, suraName: suraName};
        props.addToPlaylist(collectData);
    }

    let data = <Loader />;

    let arr = [];
    const suraLoop = () => {
        let sing= null;
        if(props.server && state.suraNames && state.suraNumbers && state.suraNumbers.length > 0 && state.suraNumbers) {
            console.log('XXX')
            if(props.playlist && props.playlist.length > 0) {
                for(let i=0; i < state.suraNumbers.length; i++) {
                    let myNumber= null;
                    let successFav = false;
                    myNumber = state.suraNumbers[i].length === 1 ? `00${state.suraNumbers[i]}` : state.suraNumbers[i].length === 2 ? `0${state.suraNumbers[i]}` : state.suraNumbers[i];
                    for (let k=0; k < props.playlist.length; k++) {
                        sing= props.getData.name === props.playlist[k].name && myNumber === props.playlist[k].number && `${props.getData.Server}/${myNumber}.mp3` === props.playlist[k].server && state.suraNames[i] === props.playlist[k].suraName;
                        if(sing){
                            successFav = sing;
                        }
                    }
                    console.log('Long');
                    arr.push(
                        <SurahBox
                            key={`${props.server}/${state.suraNames[i]}/${myNumber}`}
                            name={state.suraNames[i]}
                            server={props.getData.Server}
                            number={myNumber}
                            clicked={() => clickedHandler(state.suraNumbers[i], state.suraNames[i])}
                            addFav={(data) => addFavFun(data, myNumber, state.suraNames[i])}
                            isFavor={successFav}
                            allData={props.getData}
                            copyText={`${props.server}/${myNumber}.mp3`}
                        />
                    )
                }
            } else {
                console.log('YYY')
                for(let i=0; i < state.suraNumbers.length; i++){
                    let myNumbers= null;
                    let successFavs = false;
                    myNumbers = state.suraNumbers[i].length === 1 ? `00${state.suraNumbers[i]}` : state.suraNumbers[i].length === 2 ? `0${state.suraNumbers[i]}` : state.suraNumbers[i];
                    console.log('Short')
                    arr.push(
                        <SurahBox
                            key={`${props.server}/${state.suraNames[i]}/${myNumbers}`}
                            name={state.suraNames[i]}
                            server={props.getData.Server}
                            number={myNumbers}
                            clicked={() => clickedHandler(state.suraNumbers[i], state.suraNames[i])}
                            addFav={(data) => addFavFun(data, myNumbers, state.suraNames[i])}
                            isFavor={successFavs}
                            allData={props.getData}
                            copyText={`${props.server}/${myNumbers}.mp3`}
                        />
                    )
                }
            }
        }
        setFilterSearch(arr);
        dispatch({type: 'allSurasBoxs', allSurasBoxes: arr});
    }
    
    if(!props.loading && props.site_language) {
        data = (
            <ImamPageStyled theme={props.theme} themeStyle={props.themeStyle} site_lang={props.language.lang}>
                <div className='top-search-rewaya mb-5'>
                    <div className='top-search mb-2 mb-md-0'>
                        <Form>
                            <Form.Control type='text' placeholder={`${props.language.lang === 'ar' ? '...' + props.site_language.search + ' ' + props.site_language.surah_name : props.site_language.search + ' ' + props.site_language.surah_name + '...'}`} className='rounded-pill search-filed' onChange={e => setSearch(e.target.value)} dir='auto' />
                        </Form>
                    </div>
                </div>
                <ul className='all-surah-name'>
                    {filterSearch}
                </ul>
            </ImamPageStyled>
        )
    }

    return data;
};

const mapStateToprops = state => ({
    getData: state.getData,
    suras: state.suras,
    imamPage: state.imamPage,
    loading: state.loading,
    server: state.server,
    playlist: state.playlist,
    suraNumber: state.suraNumber,
    theme: state.theme,
    themeStyle: state.themeStyle,
    language: state.language,
    site_language: state.site_language
})

const mapDispatchToprops = dispatch => ({
    showPlayer: () => dispatch(actions.showPlayer()),
    getSurah_Number_Name: (number, suraName) => dispatch(actions.getSurah_Number_Name(number, suraName)),
    suraLink: (link) => dispatch(actions.suraLink(link)),
    playingFalse: () => dispatch(actions.playingFalse()),
    allSuras: (names) => dispatch(actions.allSurasNames(names)),
    startRequest: () => dispatch(actions.startRequest()),
    successRequest: () => dispatch(actions.successRequest()),
    failRequest: (error) => dispatch(actions.failRequest(error)),
    imamName: (name) => dispatch(actions.getImamNameBeforeUpdate(name)),
    addToPlaylist: (item) => dispatch(actions.addToPlayList(item)),
    isFromFavouritePlaylist: (bool) => dispatch(actions.isFromFavouritePlaylist(bool)),
    getSuras: () => dispatch(actions.getSuras())
})

export default connect(mapStateToprops, mapDispatchToprops)(ErrorHandler(ImamPage, axios));
