export const isFavoritePage = (value) => ({type: actionType.iS_FAVORITE_PAGE, value: value});

//  Request

export const startRequest = () => ({type: actionType.START_REQUEST});

export const successRequest = () => ({type: actionType.SUCCESS_REQUEST});

export const failRequest = (error) => ({type: actionType.FAIL_REQUEST, error: error});

export const startMain = () => ({type: actionType.START_MAIN});

export const successMain = (letters, letterIndexs, allElements) => ({type: actionType.SUCCESS_MAIN, letters: letters, letterIndexs: letterIndexs, allElements: allElements})

//  Get Data

export const getData = (data) => ({type: actionType.GET_IMAM_DATA, imamData: data, suras: data.suras.split(',')});

export const getServer = (server) => ({type: actionType.GET_SERVER, server: server});

export const showPlayer = () => ({type: actionType.SHOW_PLAYER});

export const suraLink = (link) => ({type: actionType.SURA_LINK, link: link});

export const suraLinkRandom = (randomNumber) => ({type: actionType.SURA_LINK_RANDOM, number: randomNumber});

//  Theme

export const changeTheme = () => ({type: actionType.IS_THEME_IS_DARK});

export const toggleTheme = (bool) => ({type: actionType.THEME_STORAGE, value: bool});

//  Language

export const changeLanguage = (lang) => {
    let language = null;
    if (lang === 'en') {
        language = '_english.json';
    } else if(lang === 'ar') {
        language = '_arabic.json';
    } else if(lang === 'fr') {
        language = '_france.json';
    } else if(lang === 'ru') {
        language = '_russia.json';
    } else if(lang === 'de') {
        language = '_germany.json';
    } else if(lang === 'es') {
        language = '_spain.json';
    } else if(lang === 'tr') {
        language = '_turkey.json';
    } else if(lang === 'cn') {
        return {type: actionType.CHOOSE_LANGUAGE, selectLanguage: {lang: 'en', language: '_china.json'}}
    } else if(lang === 'tl') {
        return {type: actionType.CHOOSE_LANGUAGE, selectLanguage: {lang: 'en', language: '_tagalog.json'}}
    } else if(lang === 'th') {
        language = '_tahi.json';
    } else if(lang === 'ur') {
        language = '_urdu.json';
    } else if(lang === 'bn') {
        language = '_bengali.json';
    } else if(lang === 'bs') {
        language = '_bosnia.json';
    } else if(lang === 'ug') {
        language = '_uyghur.json';
    } else if(lang === 'fa') {
        language = '_farsi.json';
    } else if(lang === 'tg') {
        language = '_tagalog.json';
    } else if(lang === 'ml') {
        language = '_malbari.json';
    } else if(lang === 'id') {
        language = '_indonesia.json';
    } else if(lang === 'pt') {
        language = '_portuguese.json';
    } else if(lang === 'ha') {
        language = '_hausa.json';
    } else if(lang === 'sw') {
        language = '_swahili.json';
    }
    return {type: actionType.CHOOSE_LANGUAGE, selectLanguage: {lang: lang, language: language}}
}

export const siteLanguage = (lang) => ({type: actionType.SITE_LANGUAGE, lang: lang});

