import React, { useState, useEffect } from 'react';
import PlayBtn from '../../components/playBtn/playBtn';
import LinkBtn from '../../components/linkBtn/linkBtn';
import { MdDelete } from 'react-icons/md';
import { connect } from 'react-redux';
import { Surahbox } from './favoriteListStyled';
import DownloadBtn from '../../components/downloadBtn/downloadBtn';
import * as actions from '../../store/action/getData';

const Playlist = props => {

    const [linkState, setLinkState] = useState(false);

    useEffect(() => {
        if(props.suraLink === props.server) {
            setLinkState(true);
        } else {
            setLinkState(false);
        }
    }, [props.suraLink]);

    const playSura = (link, number, suraName, imamName) => {
        console.log(link, number, suraName, imamName);
        console.log('Play Func');
        props.playingFalse();
        props.getImamNameBeforeUpdate(imamName);
        props.favouritePlaylistImmamName(imamName);
        props.suraLinkFunc(link);
        props.getSurah_Number_Name(number, suraName);
        props.isFromFavouritePlaylist(true);
        props.showPlayer();
    }
    console.log(props);
    return (
        <Surahbox theme={props.theme} themeStyle={props.themeStyle} linkState={linkState}>
            <div className='icon_name_number_section d-flex flex-column'>
                <div className='icon_name_number d-flex align-items-center align-items-center'>
                    <PlayBtn size={33} color='#F5B44B' clicked={() => playSura(props.server, props.number, props.suraName, props.name)} linkState={linkState} />
                    <div className='text d-flex flex-column' onClick={() => playSura(props.server, props.number, props.suraName, props.name)}>
                        <div className='d-flex surah-box-number-number-text'>
                            <div className='surah-box-number'>{props.number}</div>
                            <div className='surah-box-name text-capitalize'>{props.suraName}</div>
                        </div>
                        <span className='immam_name text-capitalize'>{props.name}</span>
                        <span className='immam_name text-capitalize'>{props.rewaya}</span>
                    </div>
                </div>
            </div>
            <div className='surah-box-imam-page-info d-flex align-items-center'>
                <div className='list m-0' title='copy link' onClick={() => navigator.clipboard.writeText(props.server)}>
                    <LinkBtn />
                </div>
                <div className='list m-0' title='delete'>
                    <button className='btn delete_item' onClick={() => props.deleteItems({server: props.server})}>
                        <MdDelete size={24} />
                    </button>
                </div>
                <div className='list m-0' title='delete'>
                    <DownloadBtn size={24} url={props.server} suraName={props.suraName} />
                </div>
            </div>
        </Surahbox>
    )
}

const mapStateToProps = state => ({
    suraLink: state.suraLink,
    theme: state.theme,
    themeStyle: state.themeStyle
});

const mapDispatchToProps = dispatch => ({
    playingFalse: () => dispatch(actions.playingFalse()),
    suraLinkFunc: (link) => dispatch(actions.suraLink(link)),
    getSurah_Number_Name: (number, suraName) => dispatch(actions.getSurah_Number_Name(number, suraName)),
    showPlayer: () => dispatch(actions.showPlayer()),
    getImamNameBeforeUpdate: (immamName) => dispatch(actions.getImamNameBeforeUpdate(immamName)),
    favouritePlaylistImmamName: (immamName) => dispatch(actions.favouritePlaylistImmamName (immamName)),
    isFromFavouritePlaylist: (bool) => dispatch(actions.isFromFavouritePlaylist(bool))
});

export default connect(mapStateToProps, mapDispatchToProps)(Playlist);
