import * as actionType from './actionTypes';

export const getData = (data) => {
    return dispatch => {
        dispatch({type: actionType.GET_IMAM_DATA, imamData: data, suras: data.suras.split(',')});
    }
}

export const getSuras = () => {
    return dispatch => {
        dispatch({type: actionType.GET_SURAS});
    }
}

export const getServer = (server) => {
    return dispatch => {
        dispatch({type: actionType.GET_SERVER, server: server});
    }
}

export const showPlayer = () => {
    return dispatch => {
        dispatch({type: actionType.SHOW_PLAYER})
    }
}

export const getSurah_Number_Name = (number, name) => {
    return dispatch => {
        dispatch({type: actionType.SURA_NUMBER_NAME, suraNumber: number, suraName: name})
    }
}

export const changeName = (name) => {
    return dispatch => {
        dispatch({type: actionType.CHANGE_NAME, name: name})
    }
}

export const allSurasNames = (names) => {
    return dispatch => {
       dispatch({type: actionType.ALL_SUARS_NAMES, surasNames: names})
    }
}

export const suraLink = (link) => {
    console.log(link)
    return dispatch => {
        dispatch({type: actionType.SURA_LINK, link: link})
    }
}

export const suraLinkRandom = (randomNumber) => {
    return dispatch => {
        dispatch({type: actionType.SURA_LINK_RANDOM, number: randomNumber})
    }
}

export const startRequest = () => {
    return dispatch => {
        dispatch({type: actionType.START_REQUEST})
    }
}

export const successRequest = () => {
    return dispatch => {
        dispatch({type: actionType.SUCCESS_REQUEST});
    }
}

export const failRequest = (error) => {
    return dispatch => {
        dispatch({type: actionType.FAIL_REQUEST, error: error})
    }
}

export const startMain = () => {
    return dispatch => {
        dispatch({type: actionType.START_MAIN});
    }
}
export const successMain = (letters, letterIndexs, allElements) => {
    return dispatch => {
        dispatch({type: actionType.SUCCESS_MAIN, letters: letters, letterIndexs: letterIndexs, allElements: allElements})
    }
}

export const closeAside = () => {
    return dispatch => {
        dispatch({type: actionType.CLOSE_ASIDE});
    }
}

export const openAside = () => {
    return {type: actionType.OPEN_ASIDE}
}

export const addToPlayList = (item) => {
    return {type: actionType.ADD_TO_PLAYLIST, item: item}
}

export const playlistStorage = (items) => {
    return {type: actionType.PLAYLIST_STORAGE, items: items}
}

export const removeItemFromPlayList = (item) => {
    return {type: actionType.REMOVE_ITEM_FROM_PLAYLIST, item: item}
}

export const deleteFavFromSurabox = (item) => {
    return {type: actionType.DELETE_FAVOURITE_FROM_SURABOX, item: item}
}

export const emptyFavFromSurabox = () => {
    return {type: actionType.EMPTY_FAVOURITE_FROM_SURABOX}
}

export const isFromFavouritePlaylist = (bool) => {
    return {type: actionType.IS_FROM_FAVOURITE_PLAYLIST, booleanValue: bool }
}

export const favouritePlaylistImmamName = (name) => {
    return {type: actionType.FAVOURITE_PLAYLIST_IMMAM_NAME, name: name }
}

export const emptySuras = () => {
    return {type: actionType.EMPTY_SURAS}
}

export const getError = (error) => {
    return {type: actionType.GET_ERROR, error: error}
}

export const changeTheme = () => {
    return {type: actionType.IS_THEME_IS_DARK}
}

export const toggleTheme = (bool) => {
    return {type: actionType.THEME_STORAGE, value: bool}
}

export const changeLanguage = (lang) => {
    let language = null;
    if (lang === 'en') {
        language = '_english.json';
    } else if(lang === 'ar') {
        language = '_arabic.json';
    } else if(lang === 'fr') {
        language = '_france.json';
    } else if(lang === 'ru') {
        language = '_russia.json';
    } else if(lang === 'de') {
        language = '_germany.json';
    } else if(lang === 'es') {
        language = '_spain.json';
    } else if(lang === 'tr') {
        language = '_turkey.json';
    } else if(lang === 'cn') {
        return {type: actionType.CHOOSE_LANGUAGE, selectLanguage: {lang: 'en', language: '_china.json'}}
    } else if(lang === 'tl') {
        return {type: actionType.CHOOSE_LANGUAGE, selectLanguage: {lang: 'en', language: '_tagalog.json'}}
    } else if(lang === 'th') {
        language = '_tahi.json';
    } else if(lang === 'ur') {
        language = '_urdu.json';
    } else if(lang === 'bn') {
        language = '_bengali.json';
    } else if(lang === 'bs') {
        language = '_bosnia.json';
    } else if(lang === 'ug') {
        language = '_uyghur.json';
    } else if(lang === 'fa') {
        language = '_farsi.json';
    } else if(lang === 'tg') {
        language = '_tagalog.json';
    } else if(lang === 'ml') {
        language = '_malbari.json';
    } else if(lang === 'id') {
        language = '_indonesia.json';
    } else if(lang === 'pt') {
        language = '_portuguese.json';
    } else if(lang === 'ha') {
        language = '_hausa.json';
    } else if(lang === 'sw') {
        language = '_swahili.json';
    }
    return {type: actionType.CHOOSE_LANGUAGE, selectLanguage: {lang: lang, language: language}}
}

export const siteLanguage = (lang) => {
    return {type: actionType.SITE_LANGUAGE, lang: lang}
}

export const isFavoritePage = (value) => {
    return {type: actionType.iS_FAVORITE_PAGE, value: value}
}

export const isRadioPage = (value) => {
    return {type: actionType.iS_RADIO_PAGE, value: value}
}
