import * as actionType from '../action/actionTypes';

const initialState = {
    isAsideShow: false,
    letters: null,
    lettersIndex: null,
    allElementsImams: null,
    data: null,
    getData: null,
    suras: [],
    server: null,
    showPlayer: false,
    suraNumber: null,
    suraName: null,
    allSurasNames: [],
    isPlaying: false,
    suraLink: null,
    loading: false,
    error: null,
    imamNameBeforeUpdate: null,
    isRandom: false,
    playlist: [],
    deleteFav: ''
}

const myReducer = (state = initialState, action) => {
    switch(action.type) {
        case actionType.START_MAIN:
            return {...state, loading: true, letters: null, lettersIndex: null, allElementsImams: null, error: null, server: null, suras: [], isRandom: false};
        case actionType.SUCCESS_MAIN:
            return {...state, loading: false, letters: action.letters, lettersIndex: action.letterIndexs, allElementsImams: action.allElements};

        case actionType.START_REQUEST:
            return {...state, loading: true, error: null, data: null};
        case actionType.SUCCESS_REQUEST:
            return {...state, loading: false};
        case actionType.FAIL_REQUEST:
            return {...state, loading: false, error: action.error};
        
        case actionType.CLOSE_ASIDE:
            return {...state, isAsideShow: false};
        case actionType.OPEN_ASIDE:
            return {...state, isAsideShow: true};

        case actionType.PLAYLIST_STORAGE:
            return {...state, playlist: action.items}

        case actionType.ADD_TO_PLAYLIST:
            let allList = state.playlist && state.playlist.some(list => list.server === action.item.server);
            // console.log(state.playlist);
            let item = null;
            if(action.item.isFavorite && !allList) {
                item = {...state, playlist: state.playlist.concat(action.item)};
            } else {
                item = {...state};
            }
            return item;
        case actionType.REMOVE_ITEM_FROM_PLAYLIST:
            let filterPlaylist = state.playlist.filter(list => list.server !== action.item.server);
            return {...state, playlist: filterPlaylist}
        case actionType.DELETE_FAVOURITE_FROM_SURABOX:
            return {...state, deleteFav: action.item}
        case actionType.EMPTY_FAVOURITE_FROM_SURABOX:
            return {...state, deleteFav: ''}

        case actionType.GET_IMAM_DATA:
            return {...state, getData: action.imamData, suras: [...action.suras], isRandom: true};
        case actionType.GET_SERVER:
            return {...state, server: action.server};
        case actionType.SHOW_PLAYER:
            return {...state, showPlayer: true};
        case actionType.SURA_NUMBER_NAME:
            return {...state, suraNumber: action.suraNumber, suraName: action.suraName};
        case actionType.CHANGE_NAME:
            return {...state, suraName: action.name};
        case actionType.GET_IMAM_NAME_BEFORE_UPDATE:
            return {...state, imamNameBeforeUpdate: action.name};
        case actionType.ALL_SUARS_NAMES:
            return {...state, allSurasNames: action.surasNames};
        case actionType.PLAYER_TOGGLE:
            return {...state, isPlaying: !state.isPlaying};
        case actionType.SURA_LINK:
            return {...state, suraLink: `${state.server}/${state.suraNumber}.mp3`};
        case actionType.SURA_LINK_RANDOM:
            return {...state, suraNumber: action.number, suraLink: `${state.server}/${action.number}.mp3`};
        case actionType.PLAYING_FALSE:
            return {...state, isPlaying: false};
        case actionType.PLAYING_TRUE:
            return {...state, isPlaying: true};


        default:
            return state;
    }
}
export default myReducer;
